import React, { useEffect, useState } from "react";
import { getBookings, updateBookingStatus } from "../../../services/api";

const Notifications = () => {
  const [pendingBookings, setPendingBookings] = useState([]);

  useEffect(() => {
    fetchPendingBookings();
  }, []);

  const fetchPendingBookings = async () => {
    try {
      const response = await getBookings();
      const pending = response.data.filter((booking) => booking.status === "pending");
      setPendingBookings(pending);
    } catch (error) {
      console.error(error);
    }
  };

  const handleAcceptBooking = async (bookingId) => {
    try {
      await updateBookingStatus(bookingId, "ongoing"); // ✅ change status to ongoing
      fetchPendingBookings();
      alert("Booking accepted!");
    } catch (error) {
      console.error("Error accepting booking:", error);
      alert("Failed to accept booking");
    }
  };

  const handleRejectBooking = async (bookingId) => {
    try {
      await updateBookingStatus(bookingId, "rejected");
      fetchPendingBookings();
      alert("Booking rejected");
    } catch (error) {
      console.error("Error rejecting booking:", error);
      alert("Failed to reject booking");
    }
  };

  return (
    <div className="p-4">
      <h2 className="font-bold text-xl mb-4">Pending Bookings</h2>
      {pendingBookings.length === 0 ? (
        <p>No pending bookings</p>
      ) : (
        pendingBookings.map((booking) => (
          <div key={booking.id} className="bg-white p-4 rounded-xl shadow mb-2 flex justify-between items-center">
            <div>
              <p><strong>{booking.user_name}</strong> wants to book <strong>{booking.service_title}</strong></p>
              <p>Amount: SC {booking.amount}</p>
            </div>
            <div className="flex gap-2">
              <button onClick={() => handleAcceptBooking(booking.id)} className="px-3 py-1 bg-green-600 text-white rounded-lg">Accept</button>
              <button onClick={() => handleRejectBooking(booking.id)} className="px-3 py-1 bg-red-600 text-white rounded-lg">Reject</button>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default Notifications;
