import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  CheckCircleIcon,
  XCircleIcon,
  EyeIcon,
  ArrowLeftIcon,
  HomeIcon,
  UsersIcon,
  WalletIcon,
  MegaphoneIcon,
  ArrowRightOnRectangleIcon,
} from "@heroicons/react/24/outline";
import api from '../../../services/api';

export default function AccountVerification() {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const sidebarItems = [
    { name: "Dashboard", icon: <HomeIcon className="w-5 h-5" />, path: "/admin-dashboard" },
    { name: "User Reports", icon: <UsersIcon className="w-5 h-5" />, path: "/user-reports" },
    { name: "Account Verification", icon: <CheckCircleIcon className="w-5 h-5" />, path: "/account-verification" },
    { name: "Wallet Logs", icon: <WalletIcon className="w-5 h-5" />, path: "/wallet-logs" },
    { name: "Post Announcement", icon: <MegaphoneIcon className="w-5 h-5" />, path: "/announcements" },
    { name: "Log Out", icon: <ArrowRightOnRectangleIcon className="h-5 w-5" />, path: "/" },
  ];

  useEffect(() => {
    fetchUnverifiedUsers();
  }, []);

  const fetchUnverifiedUsers = async () => {
    try {
      const response = await api.get('/auth/unverified-users');
      setUsers(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching users:', error);
      setLoading(false);
    }
  };

  const handleVerify = async (userId, status) => {
    try {
      await api.post(`/auth/verify-user/${userId}`, { status });
      fetchUnverifiedUsers();
      setSelectedUser(null);
    } catch (error) {
      console.error('Error verifying user:', error);
    }
  };

  return (
    <div className="flex h-screen bg-gradient-to-br from-gray-50 to-white">
      {/* Sidebar */}
      <div className="w-72 bg-white shadow-xl z-20">
        <div className="p-6 bg-gradient-to-r from-blue-600 to-indigo-600">
          <h1 className="text-xl font-bold text-white">Admin Dashboard</h1>
          <p className="text-blue-100 text-sm mt-1">System Management</p>
        </div>

        <nav className="p-4 space-y-2">
          {sidebarItems.map((item) => (
            <button
              key={item.name}
              onClick={() => navigate(item.path)}
              className="flex items-center w-full p-3 text-gray-600 hover:text-blue-600 rounded-xl transition-all duration-200 group hover:bg-gradient-to-r from-blue-50 to-indigo-50"
            >
              <div className="bg-white p-2 rounded-lg shadow-sm group-hover:scale-110 transition-transform duration-200">
                {item.icon}
              </div>
              <span className="ml-3 font-medium">{item.name}</span>
            </button>
          ))}
        </nav>
      </div>

      {/* Main Content */}
      <div className="flex-1 overflow-hidden">
        {/* Header */}
        <div className="bg-white border-b shadow-sm">
          <div className="flex items-center justify-between px-8 py-6">
            <h2 className="text-2xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-indigo-600">
              Account Verification
            </h2>
          </div>
        </div>

        <div className="p-8 overflow-y-auto h-[calc(100vh-5rem)]">
          <div className="grid grid-cols-2 gap-6">
            {/* Users List */}
            <div className="bg-white rounded-2xl shadow p-6">
              <h2 className="font-semibold mb-4">Pending Verifications</h2>
              {loading ? (
                <div>Loading...</div>
              ) : (
                <div className="space-y-4">
                  {users.map((user) => (
                    <div
                      key={user.id}
                      className={`p-4 border rounded-xl cursor-pointer transition-all ${
                        selectedUser?.id === user.id
                          ? 'border-blue-500 bg-blue-50'
                          : 'hover:border-gray-300'
                      }`}
                      onClick={() => setSelectedUser(user)}
                    >
                      <div className="flex justify-between items-center">
                        <div>
                          <h3 className="font-medium">{user.full_name}</h3>
                          <p className="text-sm text-gray-500">{user.email}</p>
                        </div>
                        <EyeIcon className="w-5 h-5 text-gray-400" />
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* User Details */}
            <div className="bg-white rounded-2xl shadow p-6">
              {selectedUser ? (
                <>
                  <h2 className="font-semibold mb-4">Verification Details</h2>
                  <div className="space-y-6">
                    {/* User Info */}
                    <div className="space-y-4">
                      <div>
                        <label className="text-sm text-gray-500">Full Name</label>
                        <p className="font-medium">{selectedUser.full_name}</p>
                      </div>
                      <div>
                        <label className="text-sm text-gray-500">Email</label>
                        <p className="font-medium">{selectedUser.email}</p>
                      </div>
                      <div>
                        <label className="text-sm text-gray-500">Course</label>
                        <p className="font-medium">{selectedUser.course}</p>
                      </div>
                      <div>
                        <label className="text-sm text-gray-500">Year Level</label>
                        <p className="font-medium">{selectedUser.year}</p>
                      </div>
                    </div>

                    {/* Documents */}
                    <div className="space-y-4">
                      <div>
                        <label className="text-sm text-gray-500">Student ID</label>
                        {selectedUser.student_id_file && (
                          <img 
                            src={`http://localhost:5000${selectedUser.student_id_file}`}
                            alt="Student ID"
                            className="mt-2 rounded-lg border w-full object-contain"
                          />
                        )}
                      </div>
                      <div>
                        <label className="text-sm text-gray-500">Study Load</label>
                        {selectedUser.study_load_file && (
                          <img 
                            src={`http://localhost:5000${selectedUser.study_load_file}`}
                            alt="Study Load"
                            className="mt-2 rounded-lg border w-full object-contain"
                          />
                        )}
                      </div>
                    </div>

                    {/* Action Buttons */}
                    <div className="flex space-x-4 pt-4 border-t">
                      <button
                        onClick={() => handleVerify(selectedUser.id, 'approved')}
                        className="flex-1 bg-green-500 text-white py-2 rounded-lg hover:bg-green-600 flex items-center justify-center space-x-2"
                      >
                        <CheckCircleIcon className="w-5 h-5" />
                        <span>Verify Account</span>
                      </button>
                      <button
                        onClick={() => handleVerify(selectedUser.id, 'rejected')}
                        className="flex-1 bg-red-500 text-white py-2 rounded-lg hover:bg-red-600 flex items-center justify-center space-x-2"
                      >
                        <XCircleIcon className="w-5 h-5" />
                        <span>Reject</span>
                      </button>
                    </div>
                  </div>
                </>
              ) : (
                <div className="text-center text-gray-500 py-8">
                  Select a user to view details
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
