import React, { useEffect } from 'react';
import { FaCheck, FaTimes } from 'react-icons/fa';

const Toast = ({ message, type = 'success', onClose, duration = 3000 }) => {
  useEffect(() => {
    const timer = setTimeout(onClose, duration);
    return () => clearTimeout(timer);
  }, [duration, onClose]);

  const bgColor = type === 'success' ? 'bg-green-500' : 'bg-red-500';
  const icon = type === 'success' ? <FaCheck /> : <FaTimes />;

  return (
    <div className={`fixed top-6 right-6 ${bgColor} text-white px-6 py-4 rounded-lg shadow-lg flex items-center space-x-3 animate-slideIn z-50 max-w-sm`}>
      <span className="text-xl">{icon}</span>
      <p className="text-sm md:text-base font-medium">{message}</p>
    </div>
  );
};

export default Toast;
