import React, { useState, useEffect } from "react";
import {
  HomeIcon,
  UserIcon,
  ChatBubbleLeftIcon,
  ClipboardDocumentListIcon,
  MagnifyingGlassIcon,
  WalletIcon,
  ReceiptRefundIcon,
  ChatBubbleOvalLeftIcon,
  ArrowRightOnRectangleIcon,
  BellIcon,
  Bars3Icon,
  XMarkIcon,
  BookmarkIcon,
  AdjustmentsHorizontalIcon,
} from "@heroicons/react/24/outline";
import { useNavigate } from "react-router-dom";
import { getAllServices, createBooking } from "../../../services/api";

// Booking Confirmation Modal
function BookingModal({ service, onClose, onConfirm }) {
  if (!service) return null;

  return (
    <div className="fixed inset-0 bg-black/60 flex items-center justify-center z-50">
      <div
        className="bg-white rounded-2xl p-6 w-[90%] max-w-md"
        onClick={(e) => e.stopPropagation()}
      >
        <h2 className="text-xl font-semibold mb-4">Confirm Booking</h2>
        <div className="space-y-4">
          <div className="bg-gray-50 p-4 rounded-xl">
            <h3 className="font-semibold text-lg">{service.title}</h3>
            <p className="text-gray-600 text-sm mt-1">{service.description}</p>
            <div className="mt-4 space-y-2">
              <div className="flex justify-between">
                <span className="text-gray-500">Price:</span>
                <span className="font-bold text-blue-600">SC {service.price}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-500">Provider:</span>
                <span className="font-medium">{service.provider}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-500">Availability:</span>
                <span className="font-medium">{service.availability}</span>
              </div>
            </div>
          </div>

          <div className="flex gap-3 pt-4">
            <button
              onClick={onClose}
              className="flex-1 px-4 py-2 text-gray-600 hover:bg-gray-100 rounded-lg"
            >
              Cancel
            </button>
            <button
              onClick={() => onConfirm(service)}
              className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
            >
              Confirm Booking
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

const FindServices = () => {
  const navigate = useNavigate();
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [services, setServices] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [filters, setFilters] = useState({
    category: "",
    minPrice: "",
    maxPrice: "",
    minRating: "",
  });
  const [showFilters, setShowFilters] = useState(false);
  const [selectedService, setSelectedService] = useState(null);

  const navItems = [
    { name: "Home", icon: <HomeIcon className="h-5 w-5" />, path: "/dashboard" },
    { name: "Profile", icon: <UserIcon className="h-5 w-5" />, path: "/profile" },
    { name: "Messages", icon: <ChatBubbleLeftIcon className="h-5 w-5" />, path: "/messages" },
    { name: "My Services", icon: <ClipboardDocumentListIcon className="h-5 w-5" />, path: "/my-services" },
    { name: "Find Services", icon: <MagnifyingGlassIcon className="h-5 w-5" />, path: "/find-services" },
    { name: "Saved", icon: <BookmarkIcon className="h-5 w-5" />, path: "/saved" },
    { name: "Wallet", icon: <WalletIcon className="h-5 w-5" />, path: "/wallet" },
    { name: "Transactions", icon: <ReceiptRefundIcon className="h-5 w-5" />, path: "/transactions" },
    { name: "Feedback", icon: <ChatBubbleOvalLeftIcon className="h-5 w-5" />, path: "/feedback" },
    { name: "Log Out", icon: <ArrowRightOnRectangleIcon className="h-5 w-5" />, path: "/" },
  ];

  const categories = ["All", "Origami", "Crocheting", "Embroidery", "Scrapbooking", "Resin Art"];

  useEffect(() => {
    fetchServices();
  }, []);

  const fetchServices = async () => {
    try {
      const user = JSON.parse(localStorage.getItem("user"));
      const response = await getAllServices(filters);
      const servicesWithProvider = response.data.map((service) => ({
        ...service,
        provider: service.user_full_name,
      }));
      const otherServices = servicesWithProvider.filter((service) => service.user_id !== user.id);
      setServices(otherServices);
    } catch (error) {
      console.error("Error fetching services:", error);
    }
  };

  const handleSearch = (e) => setSearchTerm(e.target.value);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters((prev) => ({ ...prev, [name]: value }));
  };

  const filteredServices = services.filter((service) => {
    const matchesSearch =
      service.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      service.description.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesCategory =
      !filters.category || filters.category === "All" || service.category === filters.category;

    const matchesPrice =
      (!filters.minPrice || service.price >= parseFloat(filters.minPrice)) &&
      (!filters.maxPrice || service.price <= parseFloat(filters.maxPrice));

    const matchesRating = !filters.minRating || service.rating >= parseFloat(filters.minRating);

    return matchesSearch && matchesCategory && matchesPrice && matchesRating;
  });

  const FilterPanel = () => (
    <div className="bg-white rounded-2xl p-3 shadow-sm border border-gray-100 mb-3 mt-3 mx-4">
      <div className="space-y-2">
        <div>
          <label className="text-xs font-medium text-gray-700 mb-1 block">Category</label>
          <select
            name="category"
            value={filters.category}
            onChange={handleFilterChange}
            className="w-full rounded-lg border-gray-200 text-sm"
          >
            {categories.map((category) => (
              <option key={category} value={category}>
                {category}
              </option>
            ))}
          </select>
        </div>

        <div className="grid grid-cols-2 gap-2">
          <div>
            <label className="text-xs font-medium text-gray-700 mb-1 block">Min Price</label>
            <input
              type="number"
              name="minPrice"
              value={filters.minPrice}
              onChange={handleFilterChange}
              className="w-full rounded-lg border-gray-200 text-sm"
              placeholder="Min SC"
            />
          </div>
          <div>
            <label className="text-xs font-medium text-gray-700 mb-1 block">Max Price</label>
            <input
              type="number"
              name="maxPrice"
              value={filters.maxPrice}
              onChange={handleFilterChange}
              className="w-full rounded-lg border-gray-200 text-sm"
              placeholder="Max SC"
            />
          </div>
        </div>

        <div>
          <label className="text-xs font-medium text-gray-700 mb-1 block">Minimum Rating</label>
          <select
            name="minRating"
            value={filters.minRating}
            onChange={handleFilterChange}
            className="w-full rounded-lg border-gray-200 text-sm"
          >
            <option value="">Any Rating</option>
            <option value="4">4+ Stars</option>
            <option value="4.5">4.5+ Stars</option>
            <option value="5">5 Stars</option>
          </select>
        </div>
      </div>
    </div>
  );

  const handleBookNow = (service) => setSelectedService(service);

  const handleConfirmBooking = async (service) => {
    try {
      const user = JSON.parse(localStorage.getItem("user"));
      const bookingData = {
        userId: user.id,
        serviceId: service.id,
        providerId: service.user_id,
        type: "booking",
        amount: service.price,
        status: "pending", // 🔹 pending until tutor accepts
      };
      await createBooking(bookingData);
      setSelectedService(null);
      alert("Service booked successfully! Waiting for tutor approval.");
      navigate("/transactions");
    } catch (error) {
      console.error("Booking failed:", error);
      alert("Failed to book service");
    }
  };

  return (
    <div className="bg-gray-100 min-h-screen">
      {/* Sidebar */}
      <div className={`fixed inset-0 bg-black/50 z-40 ${isSidebarOpen ? "block" : "hidden"}`}>
        <div
          className="w-64 h-full bg-white p-4"
          onClick={(e) => e.stopPropagation()}
        >
          <button onClick={() => setIsSidebarOpen(false)} className="mb-4">
            <XMarkIcon className="h-6 w-6" />
          </button>
          <ul className="space-y-2">
            {navItems.map((item) => (
              <li key={item.name} onClick={() => navigate(item.path)} className="flex items-center gap-2 p-2 hover:bg-gray-200 rounded-lg cursor-pointer">
                {item.icon} <span>{item.name}</span>
              </li>
            ))}
          </ul>
        </div>
      </div>

      {/* Top Bar */}
      <div className="flex items-center justify-between p-4 bg-white shadow-md">
        <button onClick={() => setIsSidebarOpen(true)}>
          <Bars3Icon className="h-6 w-6" />
        </button>
        <h1 className="font-bold text-xl">Find Services</h1>
        <BellIcon className="h-6 w-6 text-gray-600" />
      </div>

      {/* Search Bar */}
      <div className="p-4">
        <input
          type="text"
          placeholder="Search services..."
          value={searchTerm}
          onChange={handleSearch}
          className="w-full p-2 rounded-lg border border-gray-300"
        />
        <button
          onClick={() => setShowFilters(!showFilters)}
          className="mt-2 text-blue-600 text-sm font-medium"
        >
          {showFilters ? "Hide Filters" : "Show Filters"}
        </button>
        {showFilters && <FilterPanel />}
      </div>

      {/* Services List */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4 p-4">
        {filteredServices.map((service) => (
          <div key={service.id} className="bg-white rounded-2xl p-4 shadow-sm">
            <h3 className="font-semibold">{service.title}</h3>
            <p className="text-gray-500 text-sm">{service.description}</p>
            <p className="mt-2 font-medium">Price: SC {service.price}</p>
            <p className="text-gray-500 text-sm">Provider: {service.provider}</p>
            <button
              onClick={() => handleBookNow(service)}
              className="mt-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
            >
              Book Now
            </button>
          </div>
        ))}
      </div>

      {/* Booking Modal */}
      <BookingModal
        service={selectedService}
        onClose={() => setSelectedService(null)}
        onConfirm={handleConfirmBooking}
      />
    </div>
  );
};

export default FindServices;
