import React, { useState } from "react";
import { FiSearch } from "react-icons/fi";
import { Bars3Icon, XMarkIcon, BellIcon } from "@heroicons/react/24/outline";
import {
  HomeIcon,
  UserIcon,
  ChatBubbleLeftIcon,
  ClipboardDocumentListIcon,
  MagnifyingGlassIcon,
  WalletIcon,
  ReceiptRefundIcon,
  ChatBubbleOvalLeftIcon,
  ArrowRightOnRectangleIcon,
  BookmarkIcon,
} from "@heroicons/react/24/outline";
import { useNavigate } from 'react-router-dom';

export default function Messages() {
  const navigate = useNavigate();
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const navItems = [
    { name: "Home", icon: <HomeIcon className="h-5 w-5" />, path: "/dashboard" },
    { name: "Profile", icon: <UserIcon className="h-5 w-5" />, path: "/profile" },
    { name: "Messages", icon: <ChatBubbleLeftIcon className="h-5 w-5" />, path: "/messages" },
    { name: "My Services", icon: <ClipboardDocumentListIcon className="h-5 w-5" />, path: "/my-services" },
    { name: "Find Services", icon: <MagnifyingGlassIcon className="h-5 w-5" />, path: "/find-services" },
    { name: "Saved", icon: <BookmarkIcon className="h-5 w-5" />, path: "/saved" },
    { name: "Wallet", icon: <WalletIcon className="h-5 w-5" />, path: "/wallet" },
    { name: "Transactions", icon: <ReceiptRefundIcon className="h-5 w-5" />, path: "/transactions" },
    { name: "Feedback", icon: <ChatBubbleOvalLeftIcon className="h-5 w-5" />, path: "/feedback" },
    { name: "Log Out", icon: <ArrowRightOnRectangleIcon className="h-5 w-5" />, path: "/" },
  ];

  const chats = [
    {
      name: "Sarah Chen",
      time: "2:30 PM",
      message: "Sure! I can help with your logo design project...",
      service: "Logo Design Service",
    },
    {
      name: "Mike Johnson",
      time: "Yesterday",
      message: "Thanks for the math tutoring session!",
      service: "Math Tutoring Service",
    },
    {
      name: "Emma Davis",
      time: "11:45 AM",
      message: "When would be a good time to start the project?",
      service: "Web Development Service",
    },
    {
      name: "Alex Rivera",
      time: "Monday",
      message: "Perfect! The essay editing is complete.",
      service: "Essay Editing Service",
    },
    {
      name: "Lisa Park",
      time: "Sunday",
      message: "Great working with you on the presentation!",
      service: "Presentation Design Service",
    },
    {
      name: "Tom Wilson",
      time: "Last Week",
      message: "The coding assignment help was excellent!",
      service: "Programming Help Service",
    },
  ];

  return (
    <div className="min-h-screen bg-white flex flex-col w-full md:max-w-sm mx-auto relative">
      {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 transform ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'} bg-white w-64 transition-transform duration-300 ease-in-out z-30 md:max-w-sm shadow-lg`}>
        <div className="p-4 border-b flex justify-between items-center">
          <h2 className="font-semibold">Menu</h2>
          <button onClick={() => setIsSidebarOpen(false)}>
            <XMarkIcon className="h-6 w-6" />
          </button>
        </div>
        <nav className="p-4">
          {navItems.map((item) => (
            <button
              key={item.name}
              onClick={() => navigate(item.path)} 
              className="flex items-center space-x-3 w-full p-3 text-gray-600 hover:bg-gray-100 rounded-lg"
            >
              {item.icon}
              <span>{item.name}</span>
            </button>
          ))}
        </nav>
      </div>

      {/* Overlay */}
      {isSidebarOpen && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-50 z-20"
          onClick={() => setIsSidebarOpen(false)}
        ></div>
      )}

      {/* Header */}
      <div className="flex items-center justify-between px-4 py-3 border-b sticky top-0 bg-white z-10">
        <button onClick={() => setIsSidebarOpen(true)}>
          <Bars3Icon className="h-6 w-6 text-gray-600" />
        </button>
        <h1 className="text-lg font-semibold">Messages</h1>
        <button 
          onClick={() => navigate('/notification')}
          className="text-xl"
        >
          <BellIcon className="h-6 w-6 text-gray-600" />
        </button>
      </div>

      {/* Search */}
      <div className="px-4 py-2 sticky top-14 bg-white z-10">
        <div className="flex items-center gap-2 border rounded-lg px-3 py-2 bg-gray-50">
          <FiSearch className="text-gray-500" />
          <input
            type="text"
            placeholder="Search by user or service..."
            className="flex-1 bg-transparent outline-none text-sm"
          />
        </div>
      </div>

      {/* Chat List */}
      <div className="flex-1 overflow-y-auto">
        {chats.map((chat, index) => (
          <div
            key={index}
            className="flex items-start gap-3 px-4 py-3 border-b hover:bg-gray-50 cursor-pointer"
          >
            <img
              src="https://via.placeholder.com/40"
              alt="avatar"
              className="w-10 h-10 rounded-full border object-cover"
            />
            <div className="flex-1">
              <div className="flex justify-between items-center">
                <h2 className="font-medium">{chat.name}</h2>
                <span className="text-xs text-gray-500">{chat.time}</span>
              </div>
              <p className="text-sm text-gray-700 truncate">{chat.message}</p>
              <p className="text-xs text-gray-500">{chat.service}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
