import React, { useState, useEffect } from "react";
import {
  HomeIcon,
  UserIcon,
  ChatBubbleLeftIcon,
  ClipboardDocumentListIcon,
  MagnifyingGlassIcon,
  WalletIcon,
  ReceiptRefundIcon,
  ChatBubbleOvalLeftIcon,
  ArrowRightOnRectangleIcon,
  BellIcon,
  Bars3Icon,
  XMarkIcon,
  PencilIcon,
  TrashIcon,
  PlusIcon,
  BookmarkIcon,
  PlusCircleIcon,
  XCircleIcon,
} from "@heroicons/react/24/outline";
import { useNavigate } from "react-router-dom";
import {
  createService,
  getUserServices,
  updateService,
  deleteService,
} from "../../../services/api";

export default function MyServices() {
  const navigate = useNavigate();
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [services, setServices] = useState([]);
  const [showServiceModal, setShowServiceModal] = useState(false);
  const [editingService, setEditingService] = useState(null);
  const [userData, setUserData] = useState(null);
  const [availabilityList, setAvailabilityList] = useState([
    { date: '', startTime: '', endTime: '' }
  ]);
  const [newService, setNewService] = useState({
    title: "",
    description: "",
    price: "",
    availability: "",
    status: "Active",
  });

  const role = userData?.role?.toLowerCase() || '';

  const navItems = (() => {
    if (role === 'learner') {
      return [
        { name: "Home", icon: <HomeIcon className="h-5 w-5" />, path: "/dashboard" },
        { name: "Profile", icon: <UserIcon className="h-5 w-5" />, path: "/profile" },
        { name: "Messages", icon: <ChatBubbleLeftIcon className="h-5 w-5" />, path: "/messages" },
        { name: "Find Services", icon: <MagnifyingGlassIcon className="h-5 w-5" />, path: "/find-services" },
        { name: "Saved", icon: <BookmarkIcon className="h-5 w-5" />, path: "/saved" },
        { name: "Wallet", icon: <WalletIcon className="h-5 w-5" />, path: "/wallet" },
        { name: "Transactions", icon: <ReceiptRefundIcon className="h-5 w-5" />, path: "/transactions" },
        { name: "Past Feedbacks", icon: <ChatBubbleOvalLeftIcon className="h-5 w-5" />, path: "/view-past-feedback" },
        { name: "Log Out", icon: <ArrowRightOnRectangleIcon className="h-5 w-5" />, path: "/" },
      ];
    }

    if (role === 'tutor') {
      return [
        { name: "Home", icon: <HomeIcon className="h-5 w-5" />, path: "/dashboard" },
        { name: "Profile", icon: <UserIcon className="h-5 w-5" />, path: "/profile" },
        { name: "Messages", icon: <ChatBubbleLeftIcon className="h-5 w-5" />, path: "/messages" },
        { name: "My Services", icon: <ClipboardDocumentListIcon className="h-5 w-5" />, path: "/my-services" },
        { name: "Wallet", icon: <WalletIcon className="h-5 w-5" />, path: "/wallet" },
        { name: "Transactions", icon: <ReceiptRefundIcon className="h-5 w-5" />, path: "/transactions" },
        { name: "Past Feedbacks", icon: <ChatBubbleOvalLeftIcon className="h-5 w-5" />, path: "/view-past-feedback" },
        { name: "Log Out", icon: <ArrowRightOnRectangleIcon className="h-5 w-5" />, path: "/" },
      ];
    }

    return [
      { name: "Home", icon: <HomeIcon className="h-5 w-5" />, path: "/dashboard" },
      { name: "Profile", icon: <UserIcon className="h-5 w-5" />, path: "/profile" },
      { name: "Messages", icon: <ChatBubbleLeftIcon className="h-5 w-5" />, path: "/messages" },
      { name: "My Services", icon: <ClipboardDocumentListIcon className="h-5 w-5" />, path: "/my-services" },
      { name: "Find Services", icon: <MagnifyingGlassIcon className="h-5 w-5" />, path: "/find-services" },
      { name: "Saved", icon: <BookmarkIcon className="h-5 w-5" />, path: "/saved" },
      { name: "Wallet", icon: <WalletIcon className="h-5 w-5" />, path: "/wallet" },
      { name: "Transactions", icon: <ReceiptRefundIcon className="h-5 w-5" />, path: "/transactions" },
      { name: "Past Feedbacks", icon: <ChatBubbleOvalLeftIcon className="h-5 w-5" />, path: "/view-past-feedback" },
      { name: "Log Out", icon: <ArrowRightOnRectangleIcon className="h-5 w-5" />, path: "/" },
    ];
  })();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem('user'));
    setUserData(storedUser);
    if (storedUser?.role?.toLowerCase() === 'learner') {
      navigate('/dashboard');
      return;
    }
    fetchUserServices();
  }, [navigate]);

  const handleAvailabilityChange = (index, field, value) => {
    const newList = [...availabilityList];
    newList[index][field] = value;
    setAvailabilityList(newList);
    
    // Update the newService state with formatted availability
    const formattedAvailability = newList.map(item => 
      `date:${item.date} and time: ${item.startTime} - ${item.endTime}`
    ).join('; ');
    
    setNewService(prev => ({
      ...prev,
      availability: formattedAvailability
    }));
  };

  const addTimeSlot = () => {
    setAvailabilityList([...availabilityList, { date: '', startTime: '', endTime: '' }]);
  };

  const removeTimeSlot = (index) => {
    if (availabilityList.length > 1) {
      const newList = availabilityList.filter((_, i) => i !== index);
      setAvailabilityList(newList);
    }
  };

  const fetchUserServices = async () => {
    try {
      const user = JSON.parse(localStorage.getItem("user"));
      const response = await getUserServices(user.id);
      setServices(response.data);
    } catch (error) {
      console.error("Error fetching services:", error);
    }
  };

  const handleEdit = (service) => {
    setEditingService(service);
    // Parse the existing availability string into the array format
    const availabilityArray = service.availability.split(';').map(slot => {
      const [datePart, timePart] = slot.split(' and time: ');
      const date = datePart.replace('date:', '').trim();
      const [startTime, endTime] = timePart.split(' - ').map(t => t.trim());
      return { date, startTime, endTime };
    });
    setAvailabilityList(availabilityArray);
    setNewService({
      title: service.title,
      description: service.description,
      price: service.price.toString(),
      availability: service.availability,
      status: service.status || "Active",
    });
    setShowServiceModal(true);
  };

  const handleDelete = async (serviceId) => {
    if (window.confirm("Are you sure you want to delete this service?")) {
      try {
        await deleteService(serviceId);
        fetchUserServices();
        alert("Service deleted successfully");
      } catch (error) {
        alert("Failed to delete service");
      }
    }
  };

  const handleCreateOrUpdate = async (e) => {
    e.preventDefault();
    try {
      const user = JSON.parse(localStorage.getItem("user"));
      const formattedAvailability = availabilityList
        .filter(slot => slot.date && slot.startTime && slot.endTime)
        .map(slot => `date:${slot.date} and time: ${slot.startTime} - ${slot.endTime}`)
        .join('; ');

      const serviceData = {
        userId: user.id,
        ...newService,
        availability: formattedAvailability
      };

      if (editingService) {
        await updateService(editingService.id, serviceData);
      } else {
        await createService(serviceData);
      }

      setShowServiceModal(false);
      setNewService({
        title: "",
        description: "",
        price: "",
        availability: "",
        status: "Active",
      });
      setAvailabilityList([{ date: '', startTime: '', endTime: '' }]);
      setEditingService(null);
      fetchUserServices();
      alert(editingService ? "Service updated successfully!" : "Service created successfully!");
    } catch (error) {
      alert(editingService ? "Failed to update service" : "Failed to create service");
    }
  };

  // Keep all existing imports and state management...

const renderServiceForm = () => (
  <div className="fixed inset-0 flex items-center justify-center z-50">
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm" onClick={() => setShowServiceModal(false)} />
    <div className="bg-white w-[90%] md:max-w-sm rounded-2xl shadow-xl relative z-10 max-h-[90vh] flex flex-col">
      {/* Modal Header */}
      <div className="p-4 bg-gradient-to-r from-blue-600 to-indigo-600 rounded-t-2xl">
        <div className="flex items-center justify-between">
          <h2 className="text-lg font-semibold text-white">
            {editingService ? "Edit Service" : "Add New Service"}
          </h2>
          <button
            onClick={() => {
              setShowServiceModal(false);
              setEditingService(null);
              setNewService({
                title: "",
                description: "",
                price: "",
                availability: "",
                status: "Active",
              });
              setAvailabilityList([{ date: '', startTime: '', endTime: '' }]);
            }}
            className="text-white p-2 hover:bg-white/10 rounded-lg transition-colors"
          >
            <XMarkIcon className="h-6 w-6" />
          </button>
        </div>
      </div>

      {/* Modal Content - Scrollable */}
      <div className="flex-1 overflow-y-auto px-4">
        <form id="serviceForm" onSubmit={handleCreateOrUpdate} className="py-4 space-y-4">
          {/* Service Title */}
          <div>
            <label className="text-sm font-medium mb-1 block">Service Title</label>
            <input
              type="text"
              value={newService.title}
              onChange={(e) => setNewService(prev => ({
                ...prev,
                title: e.target.value
              }))}
              className="w-full border rounded-xl px-4 py-3"
              required
            />
          </div>

          {/* Description */}
          <div>
            <label className="text-sm font-medium mb-1 block">Description</label>
            <textarea
              value={newService.description}
              onChange={(e) => setNewService(prev => ({
                ...prev,
                description: e.target.value
              }))}
              className="w-full border rounded-xl px-4 py-3"
              rows="4"
              required
            />
          </div>

          {/* Price */}
          <div>
            <label className="text-sm font-medium mb-1 block">Price (SC)</label>
            <input
              type="number"
              value={newService.price}
              onChange={(e) => setNewService(prev => ({
                ...prev,
                price: e.target.value
              }))}
              className="w-full border rounded-xl px-4 py-3"
              required
            />
          </div>

          {/* Availability Section */}
          <div>
            <label className="text-sm font-medium mb-1 block">Availability</label>
            <div className="space-y-4">
              {availabilityList.map((slot, index) => (
                <div key={index} className="flex flex-col space-y-2 p-4 border rounded-xl relative">
                  {availabilityList.length > 1 && (
                    <button
                      type="button"
                      onClick={() => removeTimeSlot(index)}
                      className="absolute top-2 right-2 text-red-500 hover:text-red-700"
                    >
                      <XCircleIcon className="h-5 w-5" />
                    </button>
                  )}
                  
                  <div>
                    <label className="text-xs text-gray-600">Date</label>
                    <input
                      type="date"
                      value={slot.date}
                      onChange={(e) => handleAvailabilityChange(index, 'date', e.target.value)}
                      className="w-full border rounded-lg px-3 py-2 mt-1"
                      min={new Date().toISOString().split('T')[0]}
                      required
                    />
                  </div>
                  
                  <div className="grid grid-cols-2 gap-2">
                    <div>
                      <label className="text-xs text-gray-600">Start Time</label>
                      <input
                        type="time"
                        value={slot.startTime}
                        onChange={(e) => handleAvailabilityChange(index, 'startTime', e.target.value)}
                        className="w-full border rounded-lg px-3 py-2 mt-1"
                        required
                      />
                    </div>
                    
                    <div>
                      <label className="text-xs text-gray-600">End Time</label>
                      <input
                        type="time"
                        value={slot.endTime}
                        onChange={(e) => handleAvailabilityChange(index, 'endTime', e.target.value)}
                        className="w-full border rounded-lg px-3 py-2 mt-1"
                        required
                      />
                    </div>
                  </div>
                </div>
              ))}
              
              <button
                type="button"
                onClick={addTimeSlot}
                className="w-full py-2 border-2 border-dashed border-blue-500 rounded-xl text-blue-600 hover:bg-blue-50 flex items-center justify-center gap-2"
              >
                <PlusCircleIcon className="h-5 w-5" />
                Add Another Time Slot
              </button>
            </div>
          </div>
        </form>
      </div>

      {/* Modal Footer - Fixed */}
      <div className="p-4 border-t bg-gray-50 rounded-b-2xl mt-auto">
        <div className="flex gap-3">
          <button
            type="button"
            onClick={() => {
              setShowServiceModal(false);
              setEditingService(null);
              setNewService({
                title: "",
                description: "",
                price: "",
                availability: "",
                status: "Active",
              });
              setAvailabilityList([{ date: '', startTime: '', endTime: '' }]);
            }}
            className="flex-1 px-4 py-2.5 text-gray-600 bg-gray-100 rounded-xl hover:bg-gray-200"
          >
            Cancel
          </button>
          <button
            type="submit"
            form="serviceForm"
            className="flex-1 px-4 py-2.5 bg-gradient-to-r from-blue-600 to-indigo-600 text-white rounded-xl hover:from-blue-700 hover:to-indigo-700"
          >
            {editingService ? "Update Service" : "Create Service"}
          </button>
        </div>
      </div>
    </div>
  </div>
);

// Keep all existing code below this...

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white flex flex-col w-full md:max-w-sm mx-auto relative">
      {/* Sidebar */}
      <div
        className={`fixed inset-y-0 left-0 transform ${
          isSidebarOpen ? "translate-x-0" : "-translate-x-full"
        } bg-gradient-to-b from-gray-50 to-white w-64 transition-transform duration-300 ease-in-out z-30 md:max-w-sm shadow-xl border-r flex flex-col`}
      >
        <div className="p-4 bg-gradient-to-r from-blue-600 to-indigo-600 flex justify-between items-center">
          <h2 className="font-semibold text-white">Menu</h2>
          <button
            onClick={() => setIsSidebarOpen(false)}
            className="text-white hover:bg-white/10 p-1 rounded-lg transition-colors"
          >
            <XMarkIcon className="h-6 w-6" />
          </button>
        </div>
        <nav className="p-3 space-y-1 flex-1 overflow-y-auto">
          {navItems.map((item) => (
            <button
              key={item.name}
              onClick={() => navigate(item.path)}
              className="flex items-center w-full p-3 text-gray-600 hover:text-blue-600 rounded-xl transition-all duration-200 group hover:bg-gradient-to-r from-blue-50 to-indigo-50"
            >
              <div className="bg-white p-2 rounded-lg shadow-sm group-hover:scale-110 transition-transform duration-200">
                {item.icon}
              </div>
              <span className="ml-3 font-medium">{item.name}</span>
            </button>
          ))}
        </nav>
      </div>

      {/* Overlay */}
      {isSidebarOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 z-20"
          onClick={() => setIsSidebarOpen(false)}
        ></div>
      )}

      {/* Header */}
      <div className="p-6 bg-gradient-to-r from-blue-600 to-indigo-600 text-white">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <button onClick={() => setIsSidebarOpen(true)}>
              <Bars3Icon className="h-6 w-6 text-white" />
            </button>
            <h1 className="text-lg font-semibold">My Services</h1>
          </div>
          <div className="flex items-center space-x-3">
            <button onClick={() => navigate("/notification")} className="relative">
              <BellIcon className="h-6 w-6 text-white" />
              <span className="absolute -top-1 -right-1 bg-red-500 w-2 h-2 rounded-full"></span>
            </button>
            <button
              onClick={() => setShowServiceModal(true)}
              className="flex items-center gap-2 bg-white/10 backdrop-blur-sm px-3 py-2 rounded-lg text-sm hover:bg-white/20 transition-all"
            >
              <PlusIcon className="h-4 w-4" /> Add Service
            </button>
          </div>
        </div>
      </div>

      {/* Service List */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {services.map((service) => (
          <div
            key={service.id}
            className="bg-white rounded-2xl p-4 shadow-sm border border-gray-100 hover:scale-[1.02] transition-transform relative"
          >
            {/* Status Badge */}
            <span
              className={`absolute top-3 right-3 px-3 py-1 text-xs font-medium rounded-full ${
                service.status === "Active"
                  ? "bg-green-100 text-green-700"
                  : "bg-green-200 text-green-600"
              }`}
            >
              {service.status || "Active"}
            </span>
            <h2 className="font-semibold text-base">{service.title}</h2>
            <p className="text-gray-600 text-sm mt-1">{service.description}</p>
            <p className="font-semibold text-black mt-2">SC {service.price}</p>

            {/* Updated Availability Display */}
            <div className="mt-2">
              <p className="text-sm font-medium text-gray-600 mb-1">Available Day:</p>
              {service.availability.split(';').map((slot, index) => {
                const [datePart, timePart] = slot.split(' and time: ');
                const date = datePart.replace('date:', '').trim();
                const time = timePart?.trim() || '';
                
                return (
                  <div key={index} className="flex items-center text-sm text-gray-500 mb-1">
                    <span className="inline-block w-24">{date}</span>
                    <span className="ml-2">{time}</span>
                  </div>
                );
              })}
            </div>

            <div className="flex gap-3 mt-3">
              <button
                onClick={() => handleEdit(service)}
                className="text-gray-600 hover:text-black"
              >
                <PencilIcon className="h-5 w-5" />
              </button>
              <button
  onClick={() => handleDelete(service.id)}
  disabled={isDeleting}
  className={`text-gray-600 hover:text-red-600 transition-colors ${
    isDeleting ? 'opacity-50 cursor-not-allowed' : ''
  }`}
>
  <TrashIcon className={`h-5 w-5 ${isDeleting ? 'animate-pulse' : ''}`} />
</button>
            </div>
            </div>
            ))}
            </div>

            {/* Modal */}
      {showServiceModal && renderServiceForm()}
    </div>
  );
}
