import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_URL,
});

export const login = (email, password) => {
  return api.post('/auth/login', { email, password });
};

export const register = (formData) => {
  return api.post('/auth/register', formData, {
    headers: {
      'Content-Type': 'multipart/form-data',
    },
  });
};

// Add interceptor to handle tokens
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Add profile update endpoint
export const updateProfile = (userId, userData) => {
  return api.put(`/auth/update-profile/${userId}`, userData);
};

export const updateProfilePhoto = (userId, photoFile) => {
  const formData = new FormData();
  formData.append('profileImage', photoFile);
  return api.put(`/auth/update-profile-photo/${userId}`, formData, {
    headers: {
      'Content-Type': 'multipart/form-data',
    },
  });
};

export default api;
