import { useState } from "react";
import { Bell, Mail, CreditCard, Info, Calendar, Star } from "lucide-react";

export default function Notifications() {
  const [activeTab, setActiveTab] = useState("All");

  const tabs = ["All", "Unread", "Messages", "Transactions"];

  const notifications = [
    {
      id: 1,
      type: "message",
      title: "New Message",
      content: "Sarah Johnson sent you a message about the project timeline.",
      time: "2m ago",
      action: "View Message",
    },
    {
      id: 2,
      type: "payment",
      title: "Payment Received",
      content: "SG 150 payment received from Client ABC for Invoice #1234.",
      time: "15m ago",
      action: "View Transaction",
    },
    {
      id: 3,
      type: "system",
      title: "System Announcement",
      content: "Scheduled maintenance will occur tonight from 11 PM to 2 AM EST.",
      time: "1h ago",
      action: "View Details",
    },
    {
      id: 4,
      type: "reminder",
      title: "Team Meeting Reminder",
      content:
        "Don't forget about the team meeting scheduled for tomorrow at 10 AM.",
      time: "3h ago",
      action: "View Message",
    },
    {
      id: 5,
      type: "update",
      title: "Feature Update",
      content:
        "New dashboard features are now available. Check out the updated interface.",
      time: "1d ago",
      action: "View Details",
    },
  ];

  const getIcon = (type) => {
    switch (type) {
      case "message":
        return <Mail className="w-5 h-5 text-gray-600" />;
      case "payment":
        return <CreditCard className="w-5 h-5 text-green-600" />;
      case "system":
        return <Info className="w-5 h-5 text-blue-600" />;
      case "reminder":
        return <Calendar className="w-5 h-5 text-purple-600" />;
      case "update":
        return <Star className="w-5 h-5 text-yellow-600" />;
      default:
        return <Bell className="w-5 h-5" />;
    }
  };

  return (
    <div className="max-w-md mx-auto bg-white shadow-md rounded-lg p-4">
      {/* Header */}
      <div className="flex items-center justify-between border-b pb-2 mb-3">
        <h2 className="text-lg font-semibold">Notifications</h2>
        <span className="bg-red-600 text-white text-xs px-2 py-1 rounded-full">3</span>
      </div>

      {/* Tabs */}
      <div className="flex space-x-3 border-b mb-3">
        {tabs.map((tab) => (
          <button
            key={tab}
            onClick={() => setActiveTab(tab)}
            className={`pb-2 text-sm font-medium ${
              activeTab === tab
                ? "border-b-2 border-black text-black"
                : "text-gray-500"
            }`}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Mark all as read */}
      <div className="flex justify-end mb-3">
        <button className="text-sm text-blue-600">Mark all as read</button>
      </div>

      {/* Notifications List */}
      <div className="space-y-4">
        {notifications.map((n) => (
          <div
            key={n.id}
            className="flex items-start space-x-3 border-b pb-3 last:border-none"
          >
            {getIcon(n.type)}
            <div className="flex-1">
              <h3 className="font-medium text-sm">{n.title}</h3>
              <p className="text-sm text-gray-600">{n.content}</p>
              <div className="flex justify-between mt-1">
                <button className="text-blue-600 text-sm">{n.action}</button>
                <span className="text-xs text-gray-400">{n.time}</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
