// RegisterForm.jsx
import React, { useState } from 'react';
import { FaArrowLeft, FaUpload } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';

const RegisterForm = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    password: '',
    confirmPassword: '',
    course: '',
    year: '',
    role: '',
    studentIdFile: null,
    studyLoadFile: null,
  });
  const [step, setStep] = useState(1);

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleRoleChange = (role) => {
    setFormData((prev) => ({
      ...prev,
      role,
    }));
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: files[0],
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // TODO: Implement registration logic (API call)
    // You can use formData here
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-white">
      {/* Mobile-Optimized Header */}
      <div className="bg-gradient-to-r from-blue-600 to-indigo-600 text-white px-4 py-6 pb-8 rounded-b-[2rem]">
        <button
          onClick={() => (step > 1 ? setStep(step - 1) : navigate('/'))}
          className="mb-4 flex items-center space-x-2"
        >
          <FaArrowLeft size={18} />
          <span className="text-sm">{step > 1 ? 'Back' : 'Sign In'}</span>
        </button>

        <h1 className="text-xl font-bold">Create Account</h1>
        <p className="text-sm text-white/80 mt-1">Step {step} of 3</p>

        {/* Progress Steps */}
        <div className="flex gap-2 mt-4">
          {[1, 2, 3].map((stepNum) => (
            <div
              key={stepNum}
              className={`h-1 rounded-full flex-1 transition-all ${
                stepNum <= step ? 'bg-white' : 'bg-white/20'
              }`}
            />
          ))}
        </div>
      </div>

      <div className="px-4 py-6">
        {step === 1 && (
          <div className="space-y-4 animate-slideIn">
            {/* Personal Info Fields */}
            <div className="space-y-3">
              <input
                type="text"
                name="fullName"
                placeholder="Full Name"
                className="w-full bg-white px-4 py-3 rounded-xl border border-gray-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-50 text-base"
                value={formData.fullName}
                onChange={handleChange}
              />
              <div className="relative">
                <input
                  type="email"
                  name="email"
                  required
                  className="w-full bg-white border border-gray-200 px-4 py-3 rounded-xl outline-none transition-all duration-200 focus:border-blue-500 peer placeholder-transparent"
                  placeholder="Email Address"
                  value={formData.email}
                  onChange={handleChange}
                />
                <label className="absolute left-4 -top-2.5 text-sm text-gray-600 bg-white px-2 transition-all duration-200 peer-placeholder-shown:top-3 peer-placeholder-shown:text-base peer-focus:-top-2.5 peer-focus:text-sm">
                  Email Address
                </label>
              </div>

              <div className="relative">
                <input
                  type="password"
                  name="password"
                  required
                  className="w-full bg-white border border-gray-200 px-4 py-3 rounded-xl outline-none transition-all duration-200 focus:border-blue-500 peer placeholder-transparent"
                  placeholder="Password"
                  value={formData.password}
                  onChange={handleChange}
                />
                <label className="absolute left-4 -top-2.5 text-sm text-gray-600 bg-white px-2 transition-all duration-200 peer-placeholder-shown:top-3 peer-placeholder-shown:text-base peer-focus:-top-2.5 peer-focus:text-sm">
                  Password
                </label>
              </div>

              <div className="relative">
                <input
                  type="password"
                  name="confirmPassword"
                  required
                  className="w-full bg-white border border-gray-200 px-4 py-3 rounded-xl outline-none transition-all duration-200 focus:border-blue-500 peer placeholder-transparent"
                  placeholder="Confirm Password"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                />
                <label className="absolute left-4 -top-2.5 text-sm text-gray-600 bg-white px-2 transition-all duration-200 peer-placeholder-shown:top-3 peer-placeholder-shown:text-base peer-focus:-top-2.5 peer-focus:text-sm">
                  Confirm Password
                </label>
              </div>
            </div>
          </div>
        )}

        {step === 2 && (
          <div className="space-y-4 animate-slideIn">
            {/* Academic Info */}
            <div className="grid grid-cols-2 gap-3">
              <div className="relative">
                <input
                  type="text"
                  name="course"
                  required
                  className="w-full bg-white border border-gray-200 px-4 py-3 rounded-xl outline-none transition-all duration-200 focus:border-blue-500 peer placeholder-transparent"
                  placeholder="Course"
                  value={formData.course}
                  onChange={handleChange}
                />
                <label className="absolute left-4 -top-2.5 text-sm text-gray-600 bg-white px-2 transition-all duration-200 peer-placeholder-shown:top-3 peer-placeholder-shown:text-base peer-focus:-top-2.5 peer-focus:text-sm">
                  Course
                </label>
              </div>

              <div className="relative">
                <input
                  type="text"
                  name="year"
                  required
                  className="w-full bg-white border border-gray-200 px-4 py-3 rounded-xl outline-none transition-all duration-200 focus:border-blue-500 peer placeholder-transparent"
                  placeholder="Year Level"
                  value={formData.year}
                  onChange={handleChange}
                />
                <label className="absolute left-4 -top-2.5 text-sm text-gray-600 bg-white px-2 transition-all duration-200 peer-placeholder-shown:top-3 peer-placeholder-shown:text-base peer-focus:-top-2.5 peer-focus:text-sm">
                  Year Level
                </label>
              </div>
            </div>

            {/* File Upload Cards */}
            <div className="space-y-3">
              <div className="bg-white p-4 rounded-xl border-2 border-dashed border-gray-300 active:border-blue-500 touch-manipulation">
                <div className="flex flex-col items-center">
                  <div className="w-12 h-12 rounded-full bg-blue-50 flex items-center justify-center mb-2">
                    <FaUpload className="text-blue-500" />
                  </div>
                  <p className="font-medium text-sm">Upload Student ID</p>
                  <p className="text-xs text-gray-500">Tap to choose file</p>
                </div>
                <input
                  type="file"
                  className="hidden"
                  name="studentIdFile"
                  onChange={handleFileChange}
                />
              </div>

              <div className="bg-white p-4 rounded-xl border-2 border-dashed border-gray-300 active:border-blue-500 touch-manipulation">
                <div className="flex flex-col items-center">
                  <div className="w-12 h-12 rounded-full bg-blue-50 flex items-center justify-center mb-2">
                    <FaUpload className="text-blue-500" />
                  </div>
                  <p className="font-medium text-sm">Upload Study Load</p>
                  <p className="text-xs text-gray-500">Tap to choose file</p>
                </div>
                <input
                  type="file"
                  className="hidden"
                  name="studyLoadFile"
                  onChange={handleFileChange}
                />
              </div>
            </div>
          </div>
        )}

        {step === 3 && (
          <div className="space-y-3 animate-slideIn">
            {/* Role Selection Cards */}
            { [
                {
                  value: 'Learner',
                  icon: 'ðŸŽ“',
                  label: 'Learner',
                  desc: 'I need help learning a craft-based skill',
                },
                {
                  value: 'Tutor',
                  icon: 'ðŸ‘¨â€ðŸ«',
                  label: 'Tutor',
                  desc: 'I can tutor others in craft-based skills',
                },
                {
                  value: 'Both',
                  icon: 'ðŸ¤',
                  label: 'Both',
                  desc: 'I can help others and need help too',
                },
              ].map((role) => (
                <button
                  key={role.value}
                  onClick={() => handleRoleChange(role.value)}
                  className={`w-full p-4 rounded-xl border-2 active:scale-95 transition-transform touch-manipulation ${
                    formData.role === role.value
                      ? 'border-blue-500 bg-blue-50'
                      : 'border-gray-200'
                  }`}
                >
                  <div className="flex items-center">
                    <span className="text-2xl mr-3">{role.icon}</span>
                    <div className="text-left">
                      <p className="font-medium">{role.label}</p>
                      <p className="text-sm text-gray-500">{role.desc}</p>
                    </div>
                  </div>
                </button>
              ))}
          </div>
        )}

        {/* Mobile-Optimized Bottom Button */}
        <button
          onClick={step < 3 ? () => setStep(step + 1) : handleSubmit}
          className="fixed bottom-6 left-4 right-4 bg-gradient-to-r from-blue-600 to-indigo-600 text-white py-4 rounded-xl font-medium text-base active:scale-[0.98] transition-transform touch-manipulation"
        >
          {step < 3 ? 'Continue' : 'Create Account'}
        </button>
      </div>
    </div>
  );
};


export default RegisterForm;
