import React, { useState, useEffect, useRef } from "react";
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import { getMessages, sendMessage } from '../../../services/api';
import {
  ArrowLeftIcon,
  EllipsisVerticalIcon,
  PaperAirplaneIcon
} from "@heroicons/react/24/outline";
import { useNavigate } from 'react-router-dom';

export default function Convo() {
  const navigate = useNavigate();
  const { userId } = useParams();
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [otherUserTyping, setOtherUserTyping] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [otherUser, setOtherUser] = useState(null);
  const [socket, setSocket] = useState(null);
  const [loading, setLoading] = useState(true);
  const messagesEndRef = useRef(null);
  const typingTimeoutRef = useRef(null);

  useEffect(() => {
    const initializeChat = async () => {
      try {
        const user = JSON.parse(localStorage.getItem('user'));
        setCurrentUser(user);
        console.log('üë§ Current user:', user?.id);

        // Fetch other user's data
        const otherUserId = parseInt(userId);
        try {
          const response = await fetch(`http://localhost:5000/api/auth/user/${otherUserId}`);
          if (response.ok) {
            const userData = await response.json();
            setOtherUser(userData);
            console.log('üë• Other user:', userData);
          }
        } catch (error) {
          console.error('Error fetching other user:', error);
        }

        // Initialize Socket.IO
        const newSocket = io('http://localhost:5000', {
          reconnection: true,
          reconnectionDelay: 1000,
          reconnectionDelayMax: 5000,
          reconnectionAttempts: 5
        });

        newSocket.on('connect', () => {
          console.log('üîå Socket connected:', newSocket.id);
          newSocket.emit('user-online', user?.id);
          console.log('üì° Emitted user-online:', user?.id);
        });

        // Listen for incoming messages
        newSocket.on('receive-message', (messageData) => {
          console.log('üì® receive-message event received:', messageData);
          setMessages(prev => [...prev, {
            id: Date.now(),
            sender_id: messageData.senderId,
            receiver_id: messageData.receiverId,
            message: messageData.message,
            sender_name: messageData.senderName,
            created_at: new Date(messageData.created_at)
          }]);
          setOtherUserTyping(false);
        });

        // Listen for typing indicator
        newSocket.on('user-typing', (data) => {
          console.log('‚úèÔ∏è user-typing event from:', data.senderId, 'checking against userId:', parseInt(userId));
          if (data.senderId === parseInt(userId)) {
            console.log('‚úÖ Setting otherUserTyping to true');
            setOtherUserTyping(true);
          }
        });

        // Listen for stop typing
        newSocket.on('user-stop-typing', (data) => {
          console.log('üõë user-stop-typing event from:', data.senderId);
          if (data.senderId === parseInt(userId)) {
            console.log('‚úÖ Setting otherUserTyping to false');
            setOtherUserTyping(false);
          }
        });

        newSocket.on('disconnect', () => {
          console.log('‚ùå Socket disconnected');
        });

        newSocket.on('connect_error', (error) => {
          console.error('üî¥ Socket connection error:', error);
        });

        setSocket(newSocket);

        // Fetch initial messages
        if (user?.id && userId) {
          await fetchMessages(user.id, otherUserId);
        }
        setLoading(false);

        // Cleanup only on unmount
        return () => {
          console.log('üßπ Cleaning up socket');
          if (newSocket) {
            newSocket.emit('user-offline', user?.id);
            newSocket.disconnect();
          }
        };
      } catch (error) {
        console.error('Error initializing chat:', error);
        setLoading(false);
      }
    };

    initializeChat();
  }, [userId]);

  const fetchMessages = async (currentUserId, otherUserId) => {
    try {
      const data = await getMessages(currentUserId, otherUserId);
      setMessages(Array.isArray(data) ? data : []);
      scrollToBottom();
    } catch (error) {
      console.error('Error fetching messages:', error);
      setMessages([]);
    }
  };

  const scrollToBottom = () => {
    setTimeout(() => {
      messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, 100);
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages, otherUserTyping]);

  // Handle typing indicator
  const handleMessageChange = (e) => {
    const value = e.target.value;
    setNewMessage(value);

    if (!socket?.connected) {
      console.warn('‚ö†Ô∏è Socket not connected');
      return;
    }

    // Emit typing event when user has text
    if (value.trim().length > 0) {
      const typingData = {
        senderId: currentUser?.id,
        receiverId: parseInt(userId)
      };
      console.log('üì§ Emitting typing:', typingData);
      socket.emit('typing', typingData);
    }

    // Clear previous timeout
    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
    }

    // Stop typing after 2 seconds of no input
    typingTimeoutRef.current = setTimeout(() => {
      if (socket?.connected) {
        const stopData = {
          senderId: currentUser?.id,
          receiverId: parseInt(userId)
        };
        console.log('üì§ Emitting stop-typing:', stopData);
        socket.emit('stop-typing', stopData);
      }
    }, 2000);
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();

    if (!newMessage.trim()) {
      console.warn('Empty message');
      return;
    }

    // Try to reconnect if disconnected
    if (!socket?.connected) {
      console.error('Socket not connected, reconnecting...');
      socket?.connect();
      // Wait a bit for reconnection
      setTimeout(() => {
        if (!socket?.connected) {
          alert('Connection lost. Please refresh the page.');
          return;
        }
      }, 1000);
      return;
    }

    if (!currentUser) {
      console.error('No current user');
      return;
    }

    const messageData = {
      senderId: currentUser?.id,
      receiverId: parseInt(userId),
      message: newMessage.trim(),
      senderName: currentUser?.full_name
    };

    // Check role compatibility
    if (currentUser?.role?.toLowerCase() === otherUser?.role?.toLowerCase()) {
      alert('You can only message users with different roles');
      return;
    }

    try {
      // Step 1: Save to database via API
      console.log('üíæ Saving message to database...');
      await sendMessage(messageData);
      console.log('‚úÖ Message saved');

      // Step 2: Add to UI immediately
      setMessages(prev => [...prev, {
        id: Date.now(),
        sender_id: currentUser?.id,
        receiver_id: parseInt(userId),
        message: messageData.message,
        sender_name: currentUser?.full_name,
        created_at: new Date()
      }]);

      // Step 3: Emit via Socket.IO to other user
      console.log('üì§ Emitting send-message via socket:', messageData);
      socket.emit('send-message', messageData);

      setNewMessage('');
      setOtherUserTyping(false);

      // Clear typing timeout and send stop-typing
      if (typingTimeoutRef.current) {
        clearTimeout(typingTimeoutRef.current);
      }
      socket.emit('stop-typing', {
        senderId: currentUser?.id,
        receiverId: parseInt(userId)
      });

    } catch (error) {
      console.error('‚ùå Error sending message:', error);
      alert('Failed to send message: ' + (error.message || 'Unknown error'));
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white flex items-center justify-center">
        <p>Loading...</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white flex flex-col w-full md:max-w-sm mx-auto">
      {/* Header */}
      <div className="p-6 bg-gradient-to-r from-blue-600 to-indigo-600 text-white sticky top-0 z-50">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <button 
              className="text-white hover:bg-white/10 p-2 rounded-lg transition-colors"
              onClick={() => navigate(-1)}
            >
              <ArrowLeftIcon className="w-5 h-5" />
            </button>
            <div className="flex flex-col">
              <h1 className="font-semibold">{otherUser?.full_name || otherUser?.name || 'User'}</h1>
              <p className="text-xs text-white/80 h-5">
                {otherUserTyping ? '‚úèÔ∏è typing...' : 'Active now'}
              </p>
            </div>
          </div>
          <button className="text-white hover:bg-white/10 p-2 rounded-lg transition-colors">
            <EllipsisVerticalIcon className="w-5 h-5" />
          </button>
        </div>
      </div>

      {/* Chat Messages */}
      <div className="flex-1 px-4 py-3 space-y-4 overflow-y-auto">
        {messages.length === 0 ? (
          <div className="text-center py-8 text-gray-500">
            <p>No messages yet. Start the conversation!</p>
          </div>
        ) : (
          messages.map((msg, index) => {
            const isSentByCurrentUser = msg.sender_id === currentUser?.id;
            
            return (
              <div 
                key={msg.id || index}
                className={`flex ${isSentByCurrentUser ? 'justify-end' : 'gap-3'}`}
              >
                {!isSentByCurrentUser && (
                  <div className="w-8 h-8 rounded-full bg-gradient-to-br from-blue-400 to-indigo-600 flex items-center justify-center flex-shrink-0">
                    <span className="text-white text-xs font-bold">
                      {msg.sender_name?.[0]?.toUpperCase() || 'U'}
                    </span>
                  </div>
                )}
                <div className={`max-w-xs ${isSentByCurrentUser ? 'mr-2' : ''}`}>
                  <p className={`px-4 py-2 rounded-2xl shadow-sm text-sm ${
                    isSentByCurrentUser 
                      ? 'bg-blue-600 text-white' 
                      : 'bg-white border border-gray-100'
                  }`}>
                    {msg.message}
                  </p>
                  <p className={`text-xs mt-1 ${
                    isSentByCurrentUser ? 'text-right mr-2' : 'text-left ml-2'
                  } text-gray-400`}>
                    {msg.created_at ? new Date(msg.created_at).toLocaleTimeString() : 'now'}
                  </p>
                </div>
              </div>
            );
          })
        )}

        {/* Typing Indicator */}
        {otherUserTyping && (
          <div className="flex gap-3">
            <div className="w-8 h-8 rounded-full bg-gradient-to-br from-blue-400 to-indigo-600 flex items-center justify-center flex-shrink-0">
              <span className="text-white text-xs font-bold">
                {otherUser?.full_name?.[0]?.toUpperCase() || 'U'}
              </span>
            </div>
            <div className="bg-white border border-gray-100 rounded-2xl px-4 py-2">
              <div className="flex space-x-1">
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
              </div>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      {/* Message Input */}
      <div className="border-t px-4 py-3 bg-white/80 backdrop-blur-md">
        <form onSubmit={handleSendMessage} className="flex items-center gap-2 bg-gray-50 rounded-2xl p-2 border border-gray-100">
          <input
            type="text"
            placeholder="Type a message..."
            value={newMessage}
            onChange={handleMessageChange}
            className="flex-1 bg-transparent outline-none text-sm px-2"
          />
          <button 
            type="submit"
            className="bg-blue-600 text-white p-3 rounded-xl hover:bg-blue-700 transition-all disabled:opacity-50"
            disabled={!newMessage.trim()}
          >
            <PaperAirplaneIcon className="w-5 h-5" />
          </button>
        </form>
      </div>
    </div>
  );
}
