import React, { useEffect } from 'react';
import { FaCheckCircle, FaExclamationCircle, FaTimes } from 'react-icons/fa';

const Toast = ({ message, type = 'success', onClose, duration = 3000, isLoading = false }) => {
  useEffect(() => {
    if (!isLoading) {
      const timer = setTimeout(() => {
        onClose();
      }, duration);

      return () => clearTimeout(timer);
    }
  }, [duration, onClose, isLoading]);

  const bgColor = isLoading ? 'bg-white' : type === 'success' ? 'bg-green-500' : 'bg-red-500';
  const textColor = isLoading ? 'text-gray-800' : 'text-white';
  const borderColor = isLoading ? 'border border-gray-200' : '';
  
  const icon = isLoading ? (
    <div className="animate-spin">
      <div className="w-5 h-5 border-2 border-green-500 border-t-transparent rounded-full"></div>
    </div>
  ) : type === 'success' ? (
    <FaCheckCircle />
  ) : (
    <FaExclamationCircle />
  );

  return (
    <div className={`fixed top-4 right-4 md:top-6 md:right-6 ${bgColor} ${textColor} ${borderColor} px-4 md:px-6 py-3 md:py-4 rounded-lg shadow-lg flex flex-col space-y-2 animate-slideIn z-50 max-w-xs md:max-w-md overflow-hidden`}>
      <div className="flex items-center space-x-3">
        <div className={`text-xl ${isLoading ? 'text-green-500' : ''}`}>{icon}</div>
        <div className="flex-1 text-sm md:text-base font-medium">{message}</div>
        {!isLoading && (
          <button onClick={onClose} className="text-white hover:opacity-80 transition">
            <FaTimes />
          </button>
        )}
      </div>
      {isLoading && (
        <div className="w-full h-1 bg-gray-200 rounded-full overflow-hidden">
          <div 
            className="h-full bg-green-500 rounded-full"
            style={{
              animation: 'progressBar 2s ease-in-out infinite'
            }}
          ></div>
        </div>
      )}
      <style jsx>{`
        @keyframes progressBar {
          0% {
            width: 0%;
          }
          50% {
            width: 70%;
          }
          100% {
            width: 100%;
          }
        }
        @keyframes slideIn {
          from {
            transform: translateX(100%);
            opacity: 0;
          }
          to {
            transform: translateX(0);
            opacity: 1;
          }
        }
        .animate-slideIn {
          animation: slideIn 0.3s ease-out;
        }
      `}</style>
    </div>
  );
};

export default Toast;
