import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAllServices, createBooking } from '../../../services/api';

const FindServices = () => {
  const navigate = useNavigate();
  const [services, setServices] = useState([]);
  const [selectedService, setSelectedService] = useState(null);

  useEffect(() => {
    fetchServices();
  }, []);

  const fetchServices = async () => {
    try {
      const currentUser = JSON.parse(localStorage.getItem('user'));
      const response = await getAllServices();
      // Filter out user's own services
      const filteredServices = response.data.filter(
        service => service.user_id !== currentUser.id
      );
      setServices(filteredServices);
    } catch (error) {
      console.error('Error fetching services:', error);
    }
  };

  const handleConfirmBooking = async (service) => {
    try {
      const user = JSON.parse(localStorage.getItem('user'));
      const bookingData = {
        userId: user.id,
        serviceId: service.id,
        providerId: service.user_id,
        status: 'pending',
        amount: service.price,
        type: 'booking'
      };

      await createBooking(bookingData);
      setSelectedService(null);
      alert('Booking request sent! Waiting for tutor confirmation.');
      navigate('/transactions');
    } catch (error) {
      console.error('Booking error:', error);
      alert(error.response?.data?.message || 'Failed to book service.');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white">
      {/* Services Grid */}
      <div className="p-4 grid gap-4">
        {services.map((service) => (
          <div key={service.id} className="bg-white rounded-xl p-4 shadow">
            <h3 className="font-semibold">{service.title}</h3>
            <p className="text-gray-600">{service.description}</p>
            <div className="mt-4 flex justify-between items-center">
              <span className="font-bold">SC {service.price}</span>
              <button
                onClick={() => setSelectedService(service)}
                className="bg-blue-600 text-white px-4 py-2 rounded-lg"
              >
                Book Now
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Booking Modal */}
      {selectedService && (
        <div className="fixed inset-0 bg-black/60 flex items-center justify-center">
          <div className="bg-white rounded-xl p-6 w-full max-w-md m-4">
            <h2 className="text-xl font-semibold mb-4">Confirm Booking</h2>
            <div className="space-y-4">
              <div className="bg-gray-50 p-4 rounded-lg">
                <h3 className="font-semibold">{selectedService.title}</h3>
                <p className="text-gray-600">{selectedService.description}</p>
                <div className="mt-2 flex justify-between">
                  <span>Price:</span>
                  <span className="font-bold">SC {selectedService.price}</span>
                </div>
              </div>
              <div className="flex gap-3">
                <button
                  onClick={() => setSelectedService(null)}
                  className="flex-1 px-4 py-2 border rounded-lg"
                >
                  Cancel
                </button>
                <button
                  onClick={() => handleConfirmBooking(selectedService)}
                  className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-lg"
                >
                  Confirm
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default FindServices;
