// filepath: c:\Users\DELL\OneDrive\Desktop\CRAFTMEUP\client\src\component\admin\wallet_log\Wallet_Log.jsx
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {
  FunnelIcon,
  HomeIcon,
  UsersIcon,
  WalletIcon,
  MegaphoneIcon,
  CurrencyDollarIcon,
  ArrowRightOnRectangleIcon,
} from "@heroicons/react/24/outline";
import { getWalletRequests, updateWalletRequest } from '../../../services/api';

export default function WalletLogs() {
  const navigate = useNavigate();
  const [transactions, setTransactions] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const navItems = [
    { name: "Dashboard", icon: <HomeIcon className="h-5 w-5" />, path: "/admin" },
    { name: "Users", icon: <UsersIcon className="h-5 w-5" />, path: "/admin/users" },
    { name: "Wallet", icon: <WalletIcon className="h-5 w-5" />, path: "/admin/wallet" },
    { name: "Announcements", icon: <MegaphoneIcon className="h-5 w-5" />, path: "/admin/announcements" },
    { name: "Reports", icon: <CurrencyDollarIcon className="h-5 w-5" />, path: "/admin/reports" },
    { name: "Log Out", icon: <ArrowRightOnRectangleIcon className="h-5 w-5" />, path: "/" },
  ];

  useEffect(() => {
    fetchTransactions();
  }, []);

  const fetchTransactions = async () => {
    try {
      setIsLoading(true);
      setError(null);
      const response = await getWalletRequests();
      console.log('Transactions:', response.data);
      setTransactions(response.data || []);
    } catch (err) {
      console.error('Error fetching transactions:', err);
      setError('Failed to fetch transactions');
    } finally {
      setIsLoading(false);
    }
  };

  const handleUpdateStatus = async (id, status) => {
    try {
      setIsLoading(true);
      await updateWalletRequest(id, status);
      await fetchTransactions();
    } catch (err) {
      console.error('Error updating status:', err);
      alert('Failed to update status');
    } finally {
      setIsLoading(false);
    }
  };

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <div className="text-red-600">{error}</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="flex">
        {/* Sidebar */}
        <div className="w-64 bg-white shadow-lg h-screen">
          <div className="p-4 border-b">
            <h1 className="text-xl font-bold">Admin Panel</h1>
          </div>
          <nav className="space-y-1 p-4">
            {navItems.map((item) => (
              <button
                key={item.name}
                onClick={() => navigate(item.path)}
                className="flex items-center w-full p-3 text-gray-600 hover:bg-blue-50 hover:text-blue-600 rounded-lg"
              >
                {item.icon}
                <span className="ml-3">{item.name}</span>
              </button>
            ))}
          </nav>
        </div>

        {/* Main Content */}
        <div className="flex-1 p-8">
          <div className="mb-6">
            <h2 className="text-2xl font-bold text-gray-800">Wallet Transactions</h2>
          </div>

          {isLoading ? (
            <div className="text-center p-8">Loading...</div>
          ) : (
            <div className="bg-white rounded-lg shadow overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">ID</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">User</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Type</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Amount</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Reference</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Status</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Actions</th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {transactions.map((txn) => (
                    <tr key={txn.id}>
                      <td className="px-6 py-4 whitespace-nowrap text-sm">{txn.id}</td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm">{txn.user_id}</td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm">{txn.type}</td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm">{txn.amount}</td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm">{txn.reference_number}</td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`px-2 py-1 text-xs rounded-full ${
                          txn.status === 'pending' ? 'bg-yellow-100 text-yellow-800' :
                          txn.status === 'approved' ? 'bg-green-100 text-green-800' :
                          txn.status === 'rejected' ? 'bg-red-100 text-red-800' :
                          'bg-gray-100 text-gray-800'
                        }`}>
                          {txn.status}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm">
                        {txn.status === 'pending' && (
                          <div className="space-x-2">
                            <button
                              onClick={() => handleUpdateStatus(txn.id, 'approved')}
                              className="text-green-600 hover:text-green-900"
                              disabled={isLoading}
                            >
                              Approve
                            </button>
                            <button
                              onClick={() => handleUpdateStatus(txn.id, 'rejected')}
                              className="text-red-600 hover:text-red-900"
                              disabled={isLoading}
                            >
                              Reject
                            </button>
                          </div>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
