import React, { useEffect } from 'react';
import { FaCheckCircle, FaExclamationCircle, FaTimes } from 'react-icons/fa';

const Toast = ({ message, type = 'success', onClose, duration = 3000, isLoading = false }) => {
  useEffect(() => {
    if (!isLoading) {
      const timer = setTimeout(() => {
        onClose();
      }, duration);

      return () => clearTimeout(timer);
    }
  }, [duration, onClose, isLoading]);

  const bgColor = isLoading ? 'bg-blue-500' : type === 'success' ? 'bg-green-500' : 'bg-red-500';
  const icon = isLoading ? (
    <div className="animate-spin">
      <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full"></div>
    </div>
  ) : type === 'success' ? (
    <FaCheckCircle />
  ) : (
    <FaExclamationCircle />
  );

  return (
    <div className={`fixed top-4 right-4 md:top-6 md:right-6 ${bgColor} text-white px-4 md:px-6 py-3 md:py-4 rounded-lg shadow-lg flex items-center space-x-3 animate-slideIn z-50 max-w-xs md:max-w-md`}>
      <div className="text-xl">{icon}</div>
      <div className="flex-1 text-sm md:text-base">{message}</div>
      {!isLoading && (
        <button onClick={onClose} className="text-white hover:opacity-80 transition">
          <FaTimes />
        </button>
      )}
    </div>
  );
};

export default Toast;
