import React, { useState, useEffect, useRef } from "react";
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import { getMessages, sendMessage } from '../../../services/api';
import {
  ArrowLeftIcon,
  EllipsisVerticalIcon,
  PaperAirplaneIcon
} from "@heroicons/react/24/outline";
import { useNavigate } from 'react-router-dom';


export default function Convo() {
  const navigate = useNavigate();
  const { userId } = useParams();
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [currentUser, setCurrentUser] = useState(null);
  const [otherUser, setOtherUser] = useState(null);
  const [socket, setSocket] = useState(null);
  const [loading, setLoading] = useState(true);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    const initializeChat = async () => {
      try {
        const user = JSON.parse(localStorage.getItem('user'));
        setCurrentUser(user);

        // Fetch other user's data
        const otherUserId = parseInt(userId);
        try {
          const response = await fetch(`http://localhost:5000/api/auth/user/${otherUserId}`);
          if (response.ok) {
            const userData = await response.json();
            setOtherUser(userData);
          }
        } catch (error) {
          console.error('Error fetching other user:', error);
        }

        // Initialize Socket.IO
        const newSocket = io('http://localhost:5000');
        newSocket.on('connect', () => {
          newSocket.emit('user-online', user?.id);
        });

        newSocket.on('receive-message', (messageData) => {
          setMessages(prev => [...prev, messageData]);
        });

        setSocket(newSocket);

        // Fetch messages
        if (user?.id && userId) {
          await fetchMessages(user.id, otherUserId);
        }
        setLoading(false);

        return () => {
          newSocket.emit('user-offline', user?.id);
          newSocket.close();
        };
      } catch (error) {
        console.error('Error initializing chat:', error);
        setLoading(false);
      }
    };

    initializeChat();
  }, [userId]);

  const fetchMessages = async (currentUserId, otherUserId) => {
    try {
      const data = await getMessages(currentUserId, otherUserId);
      setMessages(Array.isArray(data) ? data : []);
      scrollToBottom();
    } catch (error) {
      console.error('Error fetching messages:', error);
      setMessages([]);
    }
  };

  const scrollToBottom = () => {
    setTimeout(() => {
      messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, 100);
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = async (e) => {
  e.preventDefault();
  if (!newMessage.trim() || !currentUser) {
    console.warn('Cannot send: empty message or no currentUser');
    return;
  }

  const messageData = {
    senderId: currentUser?.id,
    receiverId: parseInt(userId),
    message: newMessage,
    senderName: currentUser?.full_name
  };

  console.log('Sending message:', messageData);

  // Check role compatibility
  if (currentUser?.role?.toLowerCase() === otherUser?.role?.toLowerCase()) {
    alert('You can only message users with different roles');
    return;
  }

  try {
    // Send via API first (this saves to database)
    const response = await sendMessage(messageData);
    console.log('API Response:', response);

    if (response.success || response.messageId) {
      // Add to UI
      setMessages(prev => [...prev, {
        ...messageData,
        id: response.messageId || Date.now(),
        created_at: new Date()
      }]);

      setNewMessage('');

      // Emit via Socket.IO if connected
      if (socket?.connected) {
        socket.emit('send-message', {
          ...messageData,
          senderRole: currentUser?.role
        });
      }
    } else {
      alert('Failed to send message: ' + (response.message || 'Unknown error'));
    }
  } catch (error) {
    console.error('Error sending message:', error);
    alert('Failed to send message: ' + (error.message || 'Network error'));
  }
};

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white flex items-center justify-center">
        <p>Loading...</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white flex flex-col w-full md:max-w-sm mx-auto">
      {/* Header */}
      <div className="p-6 bg-gradient-to-r from-blue-600 to-indigo-600 text-white sticky top-0 z-50">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <button 
              className="text-white hover:bg-white/10 p-2 rounded-lg transition-colors"
              onClick={() => navigate(-1)}
            >
              <ArrowLeftIcon className="w-5 h-5" />
            </button>
            <div className="flex flex-col">
              <h1 className="font-semibold">{otherUser?.full_name || otherUser?.name || 'User'}</h1>
              <p className="text-xs text-white/80">{otherUser?.role || 'Active now'}</p>
            </div>
          </div>
          <button className="text-white hover:bg-white/10 p-2 rounded-lg transition-colors">
            <EllipsisVerticalIcon className="w-5 h-5" />
          </button>
        </div>
      </div>

      {/* Chat Messages */}
      <div className="flex-1 px-4 py-3 space-y-4 overflow-y-auto">
        {messages.length === 0 ? (
          <div className="text-center py-8 text-gray-500">
            <p>No messages yet. Start the conversation!</p>
          </div>
        ) : (
          messages.map((msg, index) => (
            <div 
              key={msg.id || index}
              className={`flex ${msg.senderId === currentUser?.id ? 'justify-end' : 'gap-3'}`}
            >
              {msg.senderId !== currentUser?.id && (
                <div className="w-8 h-8 rounded-full bg-gradient-to-br from-blue-400 to-indigo-600 flex items-center justify-center flex-shrink-0">
                  <span className="text-white text-xs font-bold">
                    {msg.senderName?.[0]?.toUpperCase() || 'U'}
                  </span>
                </div>
              )}
              <div className="max-w-xs">
                <p className={`px-4 py-2 rounded-2xl shadow-sm text-sm ${
                  msg.senderId === currentUser?.id 
                    ? 'bg-blue-600 text-white' 
                    : 'bg-white border border-gray-100'
                }`}>
                  {msg.message}
                </p>
                <p className={`text-xs mt-1 ${
                  msg.senderId === currentUser?.id ? 'text-right mr-2' : 'text-left ml-2'
                } text-gray-400`}>
                  {msg.created_at ? new Date(msg.created_at).toLocaleTimeString() : 'now'}
                </p>
              </div>
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* Message Input */}
      <div className="border-t px-4 py-3 bg-white/80 backdrop-blur-md">
        <form onSubmit={handleSendMessage} className="flex items-center gap-2 bg-gray-50 rounded-2xl p-2 border border-gray-100">
          <input
            type="text"
            placeholder="Type a message..."
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            className="flex-1 bg-transparent outline-none text-sm px-2"
          />
          <button 
            type="submit"
            className="bg-blue-600 text-white p-3 rounded-xl hover:bg-blue-700 transition-all disabled:opacity-50"
            disabled={!newMessage.trim()}
          >
            <PaperAirplaneIcon className="w-5 h-5" />
          </button>
        </form>
      </div>
    </div>
  );
}
