import React, { useState, useEffect } from "react";
import {
  HomeIcon,
  UserIcon,
  ChatBubbleLeftIcon,
  ClipboardDocumentListIcon,
  MagnifyingGlassIcon,
  WalletIcon,
  ReceiptRefundIcon,
  ChatBubbleOvalLeftIcon,
  ArrowRightOnRectangleIcon,
  BellIcon,
  Bars3Icon,
  XMarkIcon,
  PencilIcon,
  TrashIcon,
  PlusIcon,
  BookmarkIcon,
} from "@heroicons/react/24/outline";
import { useNavigate } from "react-router-dom";
import {
  createService,
  getUserServices,
  updateService,
  deleteService,
} from "../../../services/api";
import ServiceModal from "../../../services/ServiceModal";

export default function MyServices() {
  const navigate = useNavigate();
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [services, setServices] = useState([]);
  const [showServiceModal, setShowServiceModal] = useState(false);
  const [newService, setNewService] = useState({
    title: "",
    description: "",
    price: "",
    availability: "",
    status: "Active",
  });
  const [editingService, setEditingService] = useState(null);

  const navItems = [
    { name: "Home", icon: <HomeIcon className="h-5 w-5" />, path: "/dashboard" },
    { name: "Profile", icon: <UserIcon className="h-5 w-5" />, path: "/profile" },
    { name: "Messages", icon: <ChatBubbleLeftIcon className="h-5 w-5" />, path: "/messages" },
    { name: "My Services", icon: <ClipboardDocumentListIcon className="h-5 w-5" />, path: "/my-services" },
    { name: "Find Services", icon: <MagnifyingGlassIcon className="h-5 w-5" />, path: "/find-services" },
    { name: "Saved", icon: <BookmarkIcon className="h-5 w-5" />, path: "/saved" },
    { name: "Wallet", icon: <WalletIcon className="h-5 w-5" />, path: "/wallet" },
    { name: "Transactions", icon: <ReceiptRefundIcon className="h-5 w-5" />, path: "/transactions" },
    { name: "Feedback", icon: <ChatBubbleOvalLeftIcon className="h-5 w-5" />, path: "/feedback" },
    { name: "Log Out", icon: <ArrowRightOnRectangleIcon className="h-5 w-5" />, path: "/" },
  ];

  useEffect(() => {
    fetchUserServices();
  }, []);

  const fetchUserServices = async () => {
    try {
      const user = JSON.parse(localStorage.getItem("user"));
      const response = await getUserServices(user.id);
      setServices(response.data);
    } catch (error) {
      console.error("Error fetching services:", error);
    }
  };

  const handleEdit = (service) => {
    setEditingService(service);
    setNewService({
      title: service.title,
      description: service.description,
      price: service.price.toString(),
      availability: service.availability,
      status: service.status || "Active",
    });
    setShowServiceModal(true);
  };

  const handleDelete = async (serviceId) => {
    if (window.confirm("Are you sure you want to delete this service?")) {
      try {
        await deleteService(serviceId);
        fetchUserServices();
        alert("Service deleted successfully");
      } catch (error) {
        alert("Failed to delete service");
      }
    }
  };

  const handleCreateOrUpdate = async (e) => {
    e.preventDefault();
    try {
      const user = JSON.parse(localStorage.getItem("user"));
      const serviceData = {
        userId: user.id,
        title: newService.title,
        description: newService.description,
        price: parseFloat(newService.price),
        availability: newService.availability,
        status: newService.status,
      };

      if (editingService) {
        await updateService(editingService.id, serviceData);
      } else {
        await createService(serviceData);
      }

      setShowServiceModal(false);
      setNewService({
        title: "",
        description: "",
        price: "",
        availability: "",
        status: "Active",
      });
      setEditingService(null);
      fetchUserServices();
      alert(editingService ? "Service updated successfully!" : "Service created successfully!");
    } catch (error) {
      alert(editingService ? "Failed to update service" : "Failed to create service");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white flex flex-col w-full md:max-w-sm mx-auto relative">
      {/* ... Sidebar + Header unchanged ... */}

      {/* Service List */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {services.map((service) => (
          <div
            key={service.id}
            className="bg-white rounded-2xl p-4 shadow-sm border border-gray-100 hover:scale-[1.02] transition-transform relative"
          >
            <span
              className={`absolute top-3 right-3 px-3 py-1 text-xs font-medium rounded-full ${
                service.status === "Active"
                  ? "bg-green-100 text-green-700"
                  : "bg-gray-200 text-gray-600"
              }`}
            >
              {service.status || "Active"}
            </span>
            <h2 className="font-semibold text-base">{service.title}</h2>
            <p className="text-gray-600 text-sm mt-1">{service.description}</p>
            <p className="font-semibold text-black mt-2">SC {service.price}</p>
            <p className="text-gray-500 text-sm mt-1">
              Available: {service.availability}
            </p>
            <div className="flex gap-3 mt-3">
              <button
                onClick={() => handleEdit(service)}
                className="text-gray-600 hover:text-black"
              >
                <PencilIcon className="h-5 w-5" />
              </button>
              <button
                onClick={() => handleDelete(service.id)}
                className="text-gray-600 hover:text-red-600"
              >
                <TrashIcon className="h-5 w-5" />
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Modal */}
      <ServiceModal
        visible={showServiceModal}
        editingService={editingService}
        newService={newService}
        setNewService={setNewService}
        handleCreateOrUpdate={handleCreateOrUpdate}
        onClose={() => {
          setShowServiceModal(false);
          setEditingService(null);
          setNewService({
            title: "",
            description: "",
            price: "",
            availability: "",
            status: "Active",
          });
        }}
      />
    </div>
  );
}
