import React, { useState, useEffect } from "react";
import axios from 'axios';
import {
  HomeIcon,
  UserIcon,
  ChatBubbleLeftIcon,
  ClipboardDocumentListIcon,
  MagnifyingGlassIcon,
  WalletIcon,
  ReceiptRefundIcon,
  ChatBubbleOvalLeftIcon,
  ArrowRightOnRectangleIcon,
  BellIcon,
  Bars3Icon,
  XMarkIcon,
  BookmarkIcon,
} from "@heroicons/react/24/outline";
import { StarIcon } from '@heroicons/react/24/solid';
import { useNavigate, useLocation } from 'react-router-dom';
import { submitFeedback, submitReport, getUserFeedback } from '../../../services/api';

const violationTypes = {
  minor: ['spam', 'rude_message', 'irrelevant_post'],
  serious: ['harassment', 'scamming', 'fake_credentials', 'offensive_behavior'],
  abuse: ['fake_account', 'coin_misuse', 'credit_abuse']
};

const sendFeedbackNotification = async (feedbackData) => {
  try {
    await axios.post('/api/notifications', {
      userId: feedbackData.tutorId,
      type: 'feedback_received',
      title: 'New Feedback Received',
      content: `You received feedback from ${feedbackData.learnerName}:\n${feedbackData.comment}`,
    });
  } catch (error) {
    console.error('Error sending feedback notification:', error);
  }
};

function ReportUser({ booking, onClose }) {
  const [violationType, setViolationType] = useState('minor');
  const [reportReason, setReportReason] = useState('');
  const [description, setDescription] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmitReport = async (e) => {
    e.preventDefault();
    if (!reportReason) {
      alert('Please select a specific violation reason');
      return;
    }

    setIsSubmitting(true);
    try {
      const user = JSON.parse(localStorage.getItem('user'));
      const reportData = {
        reported_user_id: booking.provider_id,
        reporter_id: user.id,
        violationType: violationType,
        reason: reportReason,
        description: description.trim()
      };

      await submitReport(reportData);
      alert('Report submitted successfully');
      onClose();
    } catch (error) {
      console.error('Error submitting report:', error);
      alert('Failed to submit report. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-2xl p-4 sm:p-6 w-full max-w-md max-h-[90vh] overflow-y-auto">
        <h2 className="text-lg sm:text-xl font-semibold mb-4">Report User</h2>
        
        <form onSubmit={handleSubmitReport}>
          <div className="mb-4">
            <label className="block text-xs sm:text-sm font-medium text-gray-700 mb-2">
              Violation Type
            </label>
            <select
              value={violationType}
              onChange={(e) => {
                setViolationType(e.target.value);
                setReportReason('');
              }}
              className="w-full p-2 border rounded-lg mb-3 text-sm"
              required
            >
              <option value="minor">Minor Issue</option>
              <option value="serious">Serious Issue</option>
              <option value="abuse">Platform Abuse</option>
            </select>

            <label className="block text-xs sm:text-sm font-medium text-gray-700 mb-2">
              Specific Reason
            </label>
            <select
              value={reportReason}
              onChange={(e) => setReportReason(e.target.value)}
              className="w-full p-2 border rounded-lg text-sm"
              required
            >
              <option value="">Select a specific reason</option>
              {violationTypes[violationType].map(reason => (
                <option key={reason} value={reason}>
                  {reason.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}
                </option>
              ))}
            </select>
          </div>

          <div className="mb-4">
            <label className="block text-xs sm:text-sm font-medium text-gray-700 mb-2">
              Additional Details
            </label>
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full p-2 border rounded-lg text-sm"
              rows="4"
              placeholder="Please provide more details..."
            />
          </div>

          <div className="flex flex-col sm:flex-row justify-end gap-2 sm:gap-3">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 text-gray-600 hover:text-gray-800 text-sm border rounded-lg"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={isSubmitting}
              className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 disabled:opacity-50 text-sm"
            >
              {isSubmitting ? 'Submitting...' : 'Submit Report'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default function Feedback() {
  const navigate = useNavigate();
  const location = useLocation();
  const { booking } = location.state || {};
  
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [showReportModal, setShowReportModal] = useState(false);
  const [rating, setRating] = useState(0);
  const [hoveredRating, setHoveredRating] = useState(0);
  const [comment, setComment] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [userData, setUserData] = useState(null);

  const navItems = [
    { name: "Home", icon: <HomeIcon className="h-5 w-5" />, path: "/dashboard" },
    { name: "Profile", icon: <UserIcon className="h-5 w-5" />, path: "/profile" },
    { name: "Messages", icon: <ChatBubbleLeftIcon className="h-5 w-5" />, path: "/messages" },
    { name: "My Services", icon: <ClipboardDocumentListIcon className="h-5 w-5" />, path: "/my-services" },
    { name: "Find Services", icon: <MagnifyingGlassIcon className="h-5 w-5" />, path: "/find-services" },
    { name: "Saved", icon: <BookmarkIcon className="h-5 w-5" />, path: "/saved" },
    { name: "Wallet", icon: <WalletIcon className="h-5 w-5" />, path: "/wallet" },
    { name: "Transactions", icon: <ReceiptRefundIcon className="h-5 w-5" />, path: "/transactions" },
    { name: "Past Feedbacks", icon: <ChatBubbleOvalLeftIcon className="h-5 w-5" />, path: "/view-past-feedback" },
    { name: "Log Out", icon: <ArrowRightOnRectangleIcon className="h-5 w-5" />, path: "/" },
  ];

  useEffect(() => {
  const storedUser = JSON.parse(localStorage.getItem('user'));
  setUserData(storedUser);
  fetchFeedback(storedUser?.id);
}, []);

  useEffect(() => {
    if (!booking) {
      navigate('/transactions');
    }
  }, [booking, navigate]);

  const handleSubmitFeedback = async () => {
  if (rating === 0) {
    alert('Please select a rating');
    return;
  }

  setIsSubmitting(true);
  try {
    const user = JSON.parse(localStorage.getItem('user'));
    
    const feedbackData = {
      service_id: booking.service_id,
      user_id: user.id,
      rating: rating,
      comment: comment.trim()
    };

    // First submit the feedback
    await submitFeedback(feedbackData);

    // Then create the notification
    await sendFeedbackNotification({
      tutorId: booking.provider_id,
      learnerName: user.full_name,
      comment: comment.trim() || `Rated ${rating} stars`  // Use rating if no comment
    });

    alert('Feedback submitted successfully!');
    navigate('/view-past-feedback');
  } catch (error) {
    console.error('Error submitting feedback:', error);
    alert('Failed to submit feedback. Please try again.');
  } finally {
    setIsSubmitting(false);
  }
};


const fetchFeedback = async (userId) => {
  if (!userId || !booking?.service_id) return;
  
  try {
    const response = await getUserFeedback(userId);
    
    // Filter feedback for current service
    const currentFeedback = response.data.filter(f => 
      f.service_id === booking.service_id && 
      f.user_id === userId
    );

    if (currentFeedback.length > 0) {
      setRating(currentFeedback[0].rating);
      setComment(currentFeedback[0].comment || '');
    }
  } catch (error) {
    console.error('Error fetching feedback:', error);
  }
};

  const handleSkip = () => {
    navigate('/transactions');
  };

  if (!booking) {
    return null;
  }

  return (
    <div className="bg-gradient-to-b from-blue-50 to-white min-h-screen flex flex-col lg:flex-row w-full">
      {/* Sidebar - Desktop (always visible) */}
      <div className="hidden lg:flex fixed inset-y-0 left-0 bg-gradient-to-b from-gray-50 to-white w-64 flex-col shadow-xl border-r z-30">
        {/* Header - Fixed at top */}
        <div className="p-4 bg-gradient-to-r from-blue-600 to-indigo-600">
          <h2 className="font-semibold text-white text-lg">Menu</h2>
        </div>

        {/* Navigation - Scrollable */}
        <nav className="p-3 space-y-1 flex-1 overflow-y-auto">
          {navItems.map((item) => (
            <button
              key={item.name}
              onClick={() => navigate(item.path)}
              className="flex items-center w-full p-3 text-gray-600 hover:text-blue-600 rounded-xl transition-all duration-200 group hover:bg-gradient-to-r from-blue-50 to-indigo-50"
            >
              <div className="bg-white p-2 rounded-lg shadow-sm group-hover:scale-110 transition-transform duration-200">
                {item.icon}
              </div>
              <span className="ml-3 font-medium text-sm">{item.name}</span>
            </button>
          ))}
        </nav>
      </div>

      {/* Sidebar - Mobile (toggle-based) */}
      <div className={`fixed inset-y-0 left-0 transform ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'} bg-gradient-to-b from-gray-50 to-white w-64 transition-transform duration-300 ease-in-out z-40 lg:hidden flex flex-col shadow-xl border-r`}>
        {/* Header - Fixed at top */}
        <div className="p-4 bg-gradient-to-r from-blue-600 to-indigo-600 flex justify-between items-center">
          <h2 className="font-semibold text-white">Menu</h2>
          <button 
            onClick={() => setIsSidebarOpen(false)}
            className="text-white hover:bg-white/10 p-1 rounded-lg transition-colors"
          >
            <XMarkIcon className="h-6 w-6" />
          </button>
        </div>

        {/* Navigation - Scrollable */}
        <nav className="p-3 space-y-1 flex-1 overflow-y-auto">
          {navItems.map((item) => (
            <button
              key={item.name}
              onClick={() => {
                navigate(item.path);
                setIsSidebarOpen(false);
              }}
              className="flex items-center w-full p-3 text-gray-600 hover:text-blue-600 rounded-xl transition-all duration-200 group hover:bg-gradient-to-r from-blue-50 to-indigo-50"
            >
              <div className="bg-white p-2 rounded-lg shadow-sm group-hover:scale-110 transition-transform duration-200">
                {item.icon}
              </div>
              <span className="ml-3 font-medium text-sm">{item.name}</span>
            </button>
          ))}
        </nav>
      </div>

      {/* Overlay */}
      {isSidebarOpen && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-50 z-30 lg:hidden"
          onClick={() => setIsSidebarOpen(false)}
        ></div>
      )}

      {/* Main Content */}
      <div className="flex-1 lg:ml-64 overflow-y-auto">
        {/* Header */}
        <div className="p-4 sm:p-6 bg-gradient-to-r from-blue-600 to-indigo-600 text-white sticky top-0 z-20 shadow-lg">
          <div className="flex items-center justify-between gap-4">
            <div className="flex items-center gap-3 flex-1 min-w-0">
              <button 
                onClick={() => setIsSidebarOpen(true)}
                className="lg:hidden flex-shrink-0 hover:bg-white/10 p-2 rounded-lg transition-colors"
              >
                <Bars3Icon className="h-6 w-6" />
              </button>
              <h1 className="text-lg sm:text-xl font-semibold truncate">Rate & Review</h1>
            </div>
            <button 
              onClick={() => navigate('/notification')} 
              className="flex-shrink-0 hover:bg-white/10 p-2 rounded-lg transition-colors relative"
            >
              <BellIcon className="h-6 w-6" />
              <span className="absolute -top-1 -right-1 bg-red-500 w-2 h-2 rounded-full"></span>
            </button>
          </div>
        </div>

        {/* Content */}
        <div className="flex-1 overflow-y-auto">
          <div className="max-w-2xl mx-auto w-full px-4 py-4 sm:py-6">
            {/* Service Info Card */}
            <div className="bg-white rounded-2xl shadow-sm p-4 sm:p-5 mb-4">
              <div className="flex items-center gap-3">
                <img
                  src="https://via.placeholder.com/40"
                  alt="profile"
                  className="w-12 sm:w-14 h-12 sm:h-14 rounded-full object-cover flex-shrink-0"
                />
                <div className="flex-1 min-w-0">
                  <h2 className="font-semibold text-sm sm:text-base truncate">{booking.provider_name}</h2>
                  <p className="text-xs sm:text-sm text-gray-500 truncate">{booking.service_title}</p>
                  <p className="text-xs text-gray-400 mt-1">
                    Completed on {new Date(booking.created_at).toLocaleDateString('en-US', {
                      month: 'short',
                      day: 'numeric',
                      year: 'numeric'
                    })}
                  </p>
                </div>
              </div>
            </div>

            {/* Rating Card */}
            <div className="bg-white rounded-2xl shadow-sm p-4 sm:p-5 mb-4">
              <div className="text-center">
                <p className="text-gray-700 font-medium text-sm sm:text-base mb-4">How was your experience?</p>
                <div className="flex justify-center gap-2 sm:gap-3 mb-3">
                  {[1, 2, 3, 4, 5].map((star) => (
                    <button
                      key={star}
                      onClick={() => setRating(star)}
                      onMouseEnter={() => setHoveredRating(star)}
                      onMouseLeave={() => setHoveredRating(0)}
                      className="transition-transform hover:scale-110"
                    >
                      <StarIcon
                        className={`h-8 sm:h-10 w-8 sm:w-10 ${
                          star <= (hoveredRating || rating)
                            ? "text-yellow-400"
                            : "text-gray-300"
                        }`}
                      />
                    </button>
                  ))}
                </div>
                {rating > 0 && (
                  <p className="text-gray-500 text-xs sm:text-sm">{rating} out of 5 stars</p>
                )}
              </div>
            </div>

            {/* Comment Box */}
            <div className="mb-4">
              <label className="block text-xs sm:text-sm font-medium text-gray-700 mb-2">
                Share your experience
              </label>
              <textarea
                value={comment}
                onChange={(e) => setComment(e.target.value)}
                placeholder="Tell us about your experience with this service..."
                className="w-full border border-gray-200 rounded-xl p-3 text-xs sm:text-sm text-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500/20 focus:border-blue-500"
                rows="4"
                maxLength={1500}
              ></textarea>
              <p className="text-right text-xs text-gray-400 mt-1">
                {comment.length}/1500 characters
              </p>
            </div>

            {/* Report Button */}
            <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between gap-3 mb-4 p-3 bg-gray-50 rounded-xl">
              <p className="text-xs sm:text-sm text-gray-600">Had an issue with this service?</p>
              <button 
                className="bg-red-500 text-white px-4 py-2 text-xs sm:text-sm rounded-lg hover:bg-red-600 transition-colors whitespace-nowrap"
                onClick={() => setShowReportModal(true)}
              >
                Report
              </button>
            </div>

            {/* Submit Buttons */}
            <div className="space-y-2 sm:space-y-3 mb-6">
              <button 
                onClick={handleSubmitFeedback}
                disabled={isSubmitting || rating === 0}
                className={`w-full ${
                  isSubmitting || rating === 0
                    ? 'bg-gray-300'
                    : 'bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700'
                } text-white py-3 rounded-xl font-medium text-sm transition-all`}
              >
                {isSubmitting ? 'Submitting...' : 'Submit Feedback'}
              </button>
              <button 
                onClick={() => navigate('/view-past-feedback')}
                disabled={isSubmitting}
                className="w-full border-2 border-blue-600 text-blue-600 py-3 rounded-xl font-medium text-sm hover:bg-blue-50 transition-all disabled:opacity-50"
              >
                View Past Feedback
              </button>
              <button 
                onClick={() => navigate('/transactions')}
                disabled={isSubmitting}
                className="w-full border-2 border-gray-300 text-gray-600 py-3 rounded-xl font-medium text-sm hover:bg-gray-50 transition-all disabled:opacity-50"
              >
                Skip for Now
              </button>
            </div>

            {/* Helpful Tips */}
            <div className="border border-blue-100 rounded-xl p-3 sm:p-4 bg-blue-50 mb-6">
              <h3 className="font-medium text-xs sm:text-sm mb-2 text-blue-900"> Helpful Tips</h3>
              <p className="text-xs text-blue-700 leading-relaxed">
                Your feedback helps improve our community. Be honest and constructive
                in your review to help others make informed decisions.
              </p>
            </div>

            {/* Bottom spacing */}
            <div className="h-4 sm:h-6"></div>
          </div>
        </div>
      </div>

      {/* Report Modal */}
      {showReportModal && (
        <ReportUser 
          booking={booking}
          onClose={() => setShowReportModal(false)} 
        />
      )}
    </div>
  );
}
