import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export const getAdminDashboardData = async (token) => {
  return axios.get(`${API_URL}/api/admin/dashboard`, {
    headers: { Authorization: `Bearer ${token}` }
  });
};

export const getAdminStats = async (token) => {
  return axios.get(`${API_URL}/api/admin/stats`, {
    headers: { Authorization: `Bearer ${token}` }
  });
};

export const getAdminReports = async (token) => {
  return axios.get(`${API_URL}/api/admin/reports`, {
    headers: { Authorization: `Bearer ${token}` }
  });
};
