import React, { useState, useEffect } from "react";
import {
  HomeIcon,
  UserIcon,
  ChatBubbleLeftIcon,
  ClipboardDocumentListIcon,
  MagnifyingGlassIcon,
  WalletIcon,
  ReceiptRefundIcon,
  ChatBubbleOvalLeftIcon,
  ArrowRightOnRectangleIcon,
  BellIcon,
  Bars3Icon,
  XMarkIcon,
  BookmarkIcon,
  AdjustmentsHorizontalIcon,
} from "@heroicons/react/24/outline";
import { useNavigate } from "react-router-dom";
import { getAllServices, createBooking } from "../../../services/api";

export default function FindServices() {
  const navigate = useNavigate();
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [services, setServices] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [filters, setFilters] = useState({
    category: "",
    minPrice: "",
    maxPrice: "",
    minRating: "",
  });
  const [showFilters, setShowFilters] = useState(false);
  const [selectedService, setSelectedService] = useState(null);

  const navItems = [
    { name: "Home", icon: <HomeIcon className="h-5 w-5" />, path: "/dashboard" },
    { name: "Profile", icon: <UserIcon className="h-5 w-5" />, path: "/profile" },
    { name: "Messages", icon: <ChatBubbleLeftIcon className="h-5 w-5" />, path: "/messages" },
    { name: "My Services", icon: <ClipboardDocumentListIcon className="h-5 w-5" />, path: "/my-services" },
    { name: "Find Services", icon: <MagnifyingGlassIcon className="h-5 w-5" />, path: "/find-services" },
    { name: "Saved", icon: <BookmarkIcon className="h-5 w-5" />, path: "/saved" },
    { name: "Wallet", icon: <WalletIcon className="h-5 w-5" />, path: "/wallet" },
    { name: "Transactions", icon: <ReceiptRefundIcon className="h-5 w-5" />, path: "/transactions" },
    { name: "Feedback", icon: <ChatBubbleOvalLeftIcon className="h-5 w-5" />, path: "/feedback" },
    { name: "Log Out", icon: <ArrowRightOnRectangleIcon className="h-5 w-5" />, path: "/" },
  ];

  const categories = [
    "All",
    "Origami",
    "Crocheting",
    "Embroidery",
    "Scrapbooking",
    "Resin Art",
  ];

  // Fetch services only once (don’t refetch on price typing)
  useEffect(() => {
    fetchServices();
  }, []);

  const fetchServices = async () => {
    try {
      const response = await getAllServices();
      setServices(response.data);
    } catch (error) {
      console.error("Error fetching services:", error);
    }
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Local filtering logic
  const filteredServices = services.filter((service) => {
    const matchesSearch =
      service.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      service.description.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesCategory =
      !filters.category ||
      filters.category === "All" ||
      service.category === filters.category;

    const matchesPrice =
      (!filters.minPrice || service.price >= parseFloat(filters.minPrice)) &&
      (!filters.maxPrice || service.price <= parseFloat(filters.maxPrice));

    const matchesRating =
      !filters.minRating || service.rating >= parseFloat(filters.minRating);

    return matchesSearch && matchesCategory && matchesPrice && matchesRating;
  });

  const FilterPanel = () => (
    <div className="bg-white rounded-2xl p-3 shadow-sm border border-gray-100 mb-3 mt-3 mx-4">
      <div className="space-y-2">
        {/* Category Filter */}
        <div>
          <label className="text-xs font-medium text-gray-700 mb-1 block">
            Category
          </label>
          <select
            name="category"
            value={filters.category}
            onChange={handleFilterChange}
            className="w-full rounded-lg border-gray-200 text-sm"
          >
            {categories.map((category) => (
              <option key={category} value={category}>
                {category}
              </option>
            ))}
          </select>
        </div>

        {/* Price Range */}
        <div className="grid grid-cols-2 gap-2">
          <div>
            <label className="text-xs font-medium text-gray-700 mb-1 block">
              Min Price
            </label>
            <input
              type="number"
              name="minPrice"
              value={filters.minPrice}
              onChange={handleFilterChange}
              className="w-full rounded-lg border-gray-200 text-sm"
              placeholder="Min SC"
            />
          </div>
          <div>
            <label className="text-xs font-medium text-gray-700 mb-1 block">
              Max Price
            </label>
            <input
              type="number"
              name="maxPrice"
              value={filters.maxPrice}
              onChange={handleFilterChange}
              className="w-full rounded-lg border-gray-200 text-sm"
              placeholder="Max SC"
            />
          </div>
        </div>

        {/* Rating Filter */}
        <div>
          <label className="text-xs font-medium text-gray-700 mb-1 block">
            Minimum Rating
          </label>
          <select
            name="minRating"
            value={filters.minRating}
            onChange={handleFilterChange}
            className="w-full rounded-lg border-gray-200 text-sm"
          >
            <option value="">Any Rating</option>
            <option value="4">4+ Stars</option>
            <option value="4.5">4.5+ Stars</option>
            <option value="5">5 Stars</option>
          </select>
        </div>
      </div>
    </div>
  );

  const handleBookNow = (service) => {
    setSelectedService(service);
  };

  const handleConfirmBooking = async (service) => {
    try {
      const user = JSON.parse(localStorage.getItem("user"));
      const bookingData = {
        userId: user.id,
        serviceId: service.id,
        status: "ongoing",
      };

      await createBooking(bookingData);
      setSelectedService(null);
      alert("Service booked successfully!");
      navigate("/transactions");
    } catch (error) {
      console.error("Error booking service:", error);
      alert("Failed to book service. Please try again.");
    }
  };

  const BookingModal = ({ service, onClose }) => {
    if (!service) return null;

    return (
      <div className="fixed inset-0 bg-black/60 flex items-center justify-center z-50">
        <div
          className="bg-white rounded-2xl p-6 w-[90%] max-w-md"
          onClick={(e) => e.stopPropagation()}
        >
          <h2 className="text-xl font-semibold mb-4">Confirm Booking</h2>
          <div className="space-y-4">
            <div className="bg-gray-50 p-4 rounded-xl">
              <h3 className="font-semibold text-lg">{service.title}</h3>
              <p className="text-gray-600 text-sm mt-1">{service.description}</p>
              <div className="mt-3 flex justify-between items-center">
                <span className="text-gray-500">Provider:</span>
                <span className="font-medium">{service.provider}</span>
              </div>
              <div className="mt-2 flex justify-between items-center">
                <span className="text-gray-500">Price:</span>
                <span className="font-bold text-blue-600">SC {service.price}</span>
              </div>
            </div>

            <div className="border-t pt-4">
              <button
                onClick={() => handleConfirmBooking(service)}
                className="w-full bg-blue-600 text-white py-3 rounded-xl font-medium hover:bg-blue-700"
              >
                Confirm Booking
              </button>
              <button
                onClick={onClose}
                className="w-full mt-2 py-3 text-gray-600 hover:bg-gray-50 rounded-xl"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white flex flex-col w-full md:max-w-sm mx-auto relative">
      {/* Sidebar */}
      <div
        className={`fixed inset-y-0 left-0 transform ${
          isSidebarOpen ? "translate-x-0" : "-translate-x-full"
        } bg-gradient-to-b from-gray-50 to-white w-64 transition-transform duration-300 ease-in-out z-30 md:max-w-sm shadow-xl border-r flex flex-col`}
      >
        <div className="p-4 bg-gradient-to-r from-blue-600 to-indigo-600 flex justify-between items-center">
          <h2 className="font-semibold text-white">Menu</h2>
          <button
            onClick={() => setIsSidebarOpen(false)}
            className="text-white hover:bg-white/10 p-1 rounded-lg transition-colors"
          >
            <XMarkIcon className="h-6 w-6" />
          </button>
        </div>

        <nav className="p-3 space-y-1 flex-1 overflow-y-auto">
          {navItems.map((item) => (
            <button
              key={item.name}
              onClick={() => navigate(item.path)}
              className="flex items-center w-full p-3 text-gray-600 hover:text-blue-600 rounded-xl transition-all duration-200 group hover:bg-gradient-to-r from-blue-50 to-indigo-50"
            >
              <div className="bg-white p-2 rounded-lg shadow-sm group-hover:scale-110 transition-transform duration-200">
                {item.icon}
              </div>
              <span className="ml-3 font-medium">{item.name}</span>
            </button>
          ))}
        </nav>
      </div>

      {isSidebarOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 z-20"
          onClick={() => setIsSidebarOpen(false)}
        ></div>
      )}

      <div className="p-6 bg-gradient-to-r from-blue-600 to-indigo-600 text-white">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <button onClick={() => setIsSidebarOpen(true)}>
              <Bars3Icon className="h-6 w-6 text-white" />
            </button>
            <h1 className="text-lg font-semibold">Browse Services</h1>
          </div>
          <div className="flex items-center space-x-3">
            <button
              onClick={() => navigate("/notification")}
              className="relative"
            >
              <BellIcon className="h-6 w-6 text-white" />
              <span className="absolute -top-1 -right-1 bg-red-500 w-2 h-2 rounded-full"></span>
            </button>
            <button
              onClick={() => setShowFilters((prev) => !prev)}
              className="p-2 hover:bg-white/10 rounded-full transition-colors"
            >
              <AdjustmentsHorizontalIcon className="h-5 w-5 text-white" />
            </button>
          </div>
        </div>

        <div className="mt-4">
          <div className="flex items-center bg-white/10 backdrop-blur-sm rounded-xl px-4 py-2">
            <MagnifyingGlassIcon className="h-5 w-5 text-white/80" />
            <input
              type="text"
              value={searchTerm}
              onChange={handleSearch}
              placeholder="Search services..."
              className="bg-transparent outline-none ml-2 text-sm w-full text-white placeholder-white/70"
            />
          </div>
        </div>
      </div>

      {showFilters && <FilterPanel />}

      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {filteredServices.length > 0 ? (
          filteredServices.map((service) => (
            <div
              key={service.id}
              className="bg-white rounded-2xl p-4 shadow-sm border border-gray-100 hover:scale-[1.02] transition-transform"
            >
              <div className="flex items-center justify-between">
                <div>
                  <h2 className="font-semibold text-sm">{service.title}</h2>
                  <p className="text-gray-600 text-xs mt-1">
                    {service.description}
                  </p>
                  <p className="text-xs text-gray-500 mt-1">
                    by {service.provider}
                  </p>
                  <p className="font-semibold text-black mt-2">
                    SC {service.price}{" "}
                    <span className="text-sm text-gray-500">
                      ⭐ {service.rating}
                    </span>
                  </p>
                </div>
                <BookmarkIcon className="h-5 w-5 text-gray-400 hover:text-blue-600 cursor-pointer" />
              </div>
              <div className="flex gap-2 mt-3">
                <button className="flex-1 px-3 py-2 text-xs rounded-lg bg-blue-600 text-white hover:bg-blue-700">
                  Book Now
                </button>
                <button className="flex-1 px-3 py-2 text-xs rounded-lg border border-gray-200 hover:bg-gray-50">
                  Message
                </button>
              </div>
            </div>
          ))
        ) : (
          <div className="text-center py-8">
            <p className="text-gray-500">
              No services found matching your criteria
            </p>
          </div>
        )}

        {selectedService && (
          <BookingModal
            service={selectedService}
            onClose={() => setSelectedService(null)}
          />
        )}
      </div>
    </div>
  );
}
