import React, { useState } from "react";
import {
  MagnifyingGlassIcon,
  AdjustmentsHorizontalIcon,
  BookmarkIcon,
} from "@heroicons/react/24/outline";

// ✅ Filter Panel Component
function FilterPanel({ filters, setFilters }) {
  return (
    <div className="bg-white p-4 rounded-2xl shadow-md border border-gray-100 mb-4">
      <h3 className="font-semibold text-gray-700 mb-3">Filters</h3>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {/* Category */}
        <div>
          <label className="text-sm font-medium text-gray-600">Category</label>
          <select
            value={filters.category}
            onChange={(e) =>
              setFilters({ ...filters, category: e.target.value })
            }
            className="w-full border rounded-lg px-3 py-2 mt-1"
          >
            <option value="All">All</option>
            <option value="Crochet">Crochet</option>
            <option value="Origami">Origami</option>
            <option value="Painting">Painting</option>
          </select>
        </div>

        {/* Price Range */}
        <div>
          <label className="text-sm font-medium text-gray-600">Min Price</label>
          <input
            type="number"
            value={filters.minPrice}
            onChange={(e) =>
              setFilters({ ...filters, minPrice: e.target.value })
            }
            className="w-full border rounded-lg px-3 py-2 mt-1"
            placeholder="e.g., 100"
          />
        </div>
        <div>
          <label className="text-sm font-medium text-gray-600">Max Price</label>
          <input
            type="number"
            value={filters.maxPrice}
            onChange={(e) =>
              setFilters({ ...filters, maxPrice: e.target.value })
            }
            className="w-full border rounded-lg px-3 py-2 mt-1"
            placeholder="e.g., 500"
          />
        </div>

        {/* Rating */}
        <div>
          <label className="text-sm font-medium text-gray-600">
            Min Rating
          </label>
          <select
            value={filters.minRating}
            onChange={(e) =>
              setFilters({ ...filters, minRating: e.target.value })
            }
            className="w-full border rounded-lg px-3 py-2 mt-1"
          >
            <option value="">Any</option>
            <option value="3">3+ stars</option>
            <option value="4">4+ stars</option>
            <option value="5">5 stars</option>
          </select>
        </div>
      </div>
    </div>
  );
}

// ✅ Main Page Component
export default function ServicePage() {
  const [searchTerm, setSearchTerm] = useState("");
  const [showFilters, setShowFilters] = useState(false);
  const [filters, setFilters] = useState({
    category: "All",
    minPrice: "",
    maxPrice: "",
    minRating: "",
  });

  // Sample services
  const services = [
    {
      id: 1,
      title: "Crochet Basics",
      description: "Learn crochet patterns and techniques",
      provider: "Anna M.",
      price: 250,
      rating: 4.5,
      category: "Crochet",
    },
    {
      id: 2,
      title: "Origami Workshop",
      description: "Paper folding art for beginners",
      provider: "Kenji S.",
      price: 150,
      rating: 4.0,
      category: "Origami",
    },
    {
      id: 3,
      title: "Painting for Fun",
      description: "Acrylic painting session for students",
      provider: "Sofia L.",
      price: 400,
      rating: 5,
      category: "Painting",
    },
  ];

  // Filtering logic
  const filteredServices = services.filter((service) => {
    const matchesSearch =
      service.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      service.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory =
      !filters.category ||
      filters.category === "All" ||
      service.category === filters.category;
    const matchesPrice =
      (!filters.minPrice || service.price >= parseFloat(filters.minPrice)) &&
      (!filters.maxPrice || service.price <= parseFloat(filters.maxPrice));
    const matchesRating =
      !filters.minRating || service.rating >= parseFloat(filters.minRating);

    return matchesSearch && matchesCategory && matchesPrice && matchesRating;
  });

  return (
    <div className="flex flex-col h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-blue-600 p-4 flex items-center justify-between shadow-md">
        <div className="flex items-center gap-2 flex-1">
          <MagnifyingGlassIcon className="h-5 w-5 text-white" />
          <input
            type="text"
            placeholder="Search services..."
            className="bg-blue-500 text-white placeholder-white/70 px-3 py-2 rounded-lg w-full"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <button
          onClick={() => setShowFilters((prev) => !prev)}
          className="p-2 hover:bg-white/10 rounded-full transition-colors"
        >
          <AdjustmentsHorizontalIcon className="h-5 w-5 text-white" />
        </button>
      </div>

      {/* Main Content */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {/* Filter Panel */}
        {showFilters && <FilterPanel filters={filters} setFilters={setFilters} />}

        {/* Service List */}
        {filteredServices.length > 0 ? (
          filteredServices.map((service) => (
            <div
              key={service.id}
              className="bg-white rounded-2xl p-4 shadow-sm border border-gray-100 hover:scale-[1.02] transition-transform"
            >
              <div className="flex items-center justify-between">
                <div>
                  <h2 className="font-semibold text-sm">{service.title}</h2>
                  <p className="text-gray-600 text-xs mt-1">
                    {service.description}
                  </p>
                  <p className="text-xs text-gray-500 mt-1">
                    by {service.provider}
                  </p>
                  <p className="font-semibold text-black mt-2">
                    SC {service.price}{" "}
                    <span className="text-sm text-gray-500">
                      ⭐ {service.rating}
                    </span>
                  </p>
                </div>
                <BookmarkIcon className="h-5 w-5 text-gray-400 hover:text-blue-600 cursor-pointer" />
              </div>
              <div className="flex gap-2 mt-3">
                <button className="flex-1 px-3 py-2 text-xs rounded-lg bg-blue-600 text-white hover:bg-blue-700">
                  Book Now
                </button>
                <button className="flex-1 px-3 py-2 text-xs rounded-lg border border-gray-200 hover:bg-gray-50">
                  Message
                </button>
              </div>
            </div>
          ))
        ) : (
          <div className="text-center py-8">
            <p className="text-gray-500">
              No services found matching your criteria
            </p>
          </div>
        )}
      </div>
    </div>
  );
}
