import React, { useState } from "react";
import {
  HomeIcon,
  UserIcon,
  ChatBubbleLeftIcon,
  ClipboardDocumentListIcon,
  MagnifyingGlassIcon,
  WalletIcon,
  ReceiptRefundIcon,
  ChatBubbleOvalLeftIcon,
  ArrowRightOnRectangleIcon,
  BellIcon,
  Bars3Icon,
  XMarkIcon,
  BookmarkIcon,
  HeartIcon,
  ChevronDownIcon,
} from "@heroicons/react/24/outline";
import { useNavigate } from 'react-router-dom';

export default function Saved() {
  const navigate = useNavigate();
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const navItems = (() => {
      if (role === 'learner') {
        // Learners: can see Find Services and Saved, cannot see My Services or add services
        return [
          { name: "Home", icon: <HomeIcon className="h-5 w-5" />, path: "/dashboard" },
          { name: "Profile", icon: <UserIcon className="h-5 w-5" />, path: "/profile" },
          { name: "Messages", icon: <ChatBubbleLeftIcon className="h-5 w-5" />, path: "/messages" },
          { name: "Find Services", icon: <MagnifyingGlassIcon className="h-5 w-5" />, path: "/find-services" },
          { name: "Saved", icon: <BookmarkIcon className="h-5 w-5" />, path: "/saved" },
          { name: "Wallet", icon: <WalletIcon className="h-5 w-5" />, path: "/wallet" },
          { name: "Transactions", icon: <ReceiptRefundIcon className="h-5 w-5" />, path: "/transactions" },
          { name: "Past Feedbacks", icon: <ChatBubbleOvalLeftIcon className="h-5 w-5" />, path: "/view-past-feedback" },
          { name: "Log Out", icon: <ArrowRightOnRectangleIcon className="h-5 w-5" />, path: "/" },
        ];
      }
  
      if (role === 'tutor') {
        // Tutors: can see My Services and manage/add them, cannot see Find Services or Saved
        return [
          { name: "Home", icon: <HomeIcon className="h-5 w-5" />, path: "/dashboard" },
          { name: "Profile", icon: <UserIcon className="h-5 w-5" />, path: "/profile" },
          { name: "Messages", icon: <ChatBubbleLeftIcon className="h-5 w-5" />, path: "/messages" },
          { name: "My Services", icon: <ClipboardDocumentListIcon className="h-5 w-5" />, path: "/my-services" },
          { name: "Wallet", icon: <WalletIcon className="h-5 w-5" />, path: "/wallet" },
          { name: "Transactions", icon: <ReceiptRefundIcon className="h-5 w-5" />, path: "/transactions" },
          { name: "Past Feedbacks", icon: <ChatBubbleOvalLeftIcon className="h-5 w-5" />, path: "/view-past-feedback" },
          { name: "Log Out", icon: <ArrowRightOnRectangleIcon className="h-5 w-5" />, path: "/" },
        ];
      }
  
      // role === 'both' or unknown: show all features (both learner + tutor capabilities)
      return [
        { name: "Home", icon: <HomeIcon className="h-5 w-5" />, path: "/dashboard" },
        { name: "Profile", icon: <UserIcon className="h-5 w-5" />, path: "/profile" },
        { name: "Messages", icon: <ChatBubbleLeftIcon className="h-5 w-5" />, path: "/messages" },
        { name: "My Services", icon: <ClipboardDocumentListIcon className="h-5 w-5" />, path: "/my-services" },
        { name: "Find Services", icon: <MagnifyingGlassIcon className="h-5 w-5" />, path: "/find-services" },
        { name: "Saved", icon: <BookmarkIcon className="h-5 w-5" />, path: "/saved" },
        { name: "Wallet", icon: <WalletIcon className="h-5 w-5" />, path: "/wallet" },
        { name: "Transactions", icon: <ReceiptRefundIcon className="h-5 w-5" />, path: "/transactions" },
        { name: "Past Feedbacks", icon: <ChatBubbleOvalLeftIcon className="h-5 w-5" />, path: "/view-past-feedback" },
        { name: "Log Out", icon: <ArrowRightOnRectangleIcon className="h-5 w-5" />, path: "/" },
      ];
    })();


  useEffect(() => {
  const user = JSON.parse(localStorage.getItem('user'));
  const role = user?.role?.toLowerCase();
  if (role === 'tutor') {
    navigate('/dashboard');
    return;
  }
}, [navigate]);

  useEffect(() => {
  const user = JSON.parse(localStorage.getItem('user'));
  if (user?.role?.toLowerCase() === 'learner') {
    navigate('/dashboard');
    return;
  }
}, [navigate]);
  const saved = [
    {
      title: "Origami Basics & Decorative Folding",
      category: "Origami Services",
      price: "SC 150/session",
      rating: "4.8",
      duration: "2-3 hrs",
      tag: "Crafting"
    },
    {
      title: "Crocheting for Beginners",
      category: "Crochet",
      price: "SC 150/hour",
      rating: "4.9",
      duration: "1-2 hrs",
      tag: "Crafting"
    },
    {
      title: "Hand Embroidery Essentials",
      category: "Hand Embroidery",
      price: "SC 150/visit",
      rating: "4.7",
      duration: "3-4 hrs",
      tag: "Crafting"
    },
    {
      title: "DIY Scrapbooking Techniques",
      category: "Scrapbooking",
      price: "SC 150/session",
      rating: "4.6",
      duration: "2 hrs",
      tag: "Crafting"
    },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white flex flex-col w-full md:max-w-sm mx-auto relative">
      {/* Sidebar - Updated to match Dashboard */}
      <div className={`fixed inset-y-0 left-0 transform ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'} bg-gradient-to-b from-gray-50 to-white w-64 transition-transform duration-300 ease-in-out z-30 md:max-w-sm shadow-xl border-r flex flex-col`}>
        {/* Header */}
        <div className="p-4 bg-gradient-to-r from-blue-600 to-indigo-600 flex justify-between items-center">
          <h2 className="font-semibold text-white">Menu</h2>
          <button 
            onClick={() => setIsSidebarOpen(false)}
            className="text-white hover:bg-white/10 p-1 rounded-lg transition-colors"
          >
            <XMarkIcon className="h-6 w-6" />
          </button>
        </div>

        {/* Navigation - Scrollable */}
        <nav className="p-3 space-y-1 flex-1 overflow-y-auto">
          {navItems.map((item) => (
            <button
              key={item.name}
              onClick={() => navigate(item.path)}
              className="flex items-center w-full p-3 text-gray-600 hover:text-blue-600 rounded-xl transition-all duration-200 group hover:bg-gradient-to-r from-blue-50 to-indigo-50"
