// LoginForm.jsx
import React, { useState, useEffect } from 'react';
import { FaEnvelope, FaEye, FaEyeSlash } from 'react-icons/fa';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const LoginForm = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    userType: 'user' // Add userType to track if it's user or admin
  });
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [isMobile, setIsMobile] = useState(false);
  const navigate = useNavigate();

  // Detect if device is mobile
  useEffect(() => {
    const checkIfMobile = () => {
      setIsMobile(window.innerWidth <= 768);
    };
    
    checkIfMobile();
    window.addEventListener('resize', checkIfMobile);
    
    return () => window.removeEventListener('resize', checkIfMobile);
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Modified handleSubmit to handle different user types
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', formData);
      if (response.data.token) {
        localStorage.setItem('token', response.data.token);
        localStorage.setItem('userType', formData.userType);
        // Redirect based on user type
        navigate(formData.userType === 'admin' ? '/admin-dashboard' : '/dashboard');
      }
    } catch (err) {
      setError(err.response?.data?.message || 'An error occurred');
    }
  };

  // Mobile Layout
  const MobileLayout = () => (
    <div className="min-h-screen flex flex-col bg-white w-full md:max-w-sm mx-auto">
      <div className="p-6 flex-1 flex flex-col justify-center">
        <h2 className="text-2xl font-semibold mb-2">Welcome Back</h2>
        <p className="text-sm text-gray-500 mb-6">Sign in to your account</p>

        {error && <div className="text-red-500 text-sm mb-4">{error}</div>}

        <form className="space-y-4" onSubmit={handleSubmit}>
          <div>
            <label className="block text-sm font-medium mb-1">Email</label>
            <div className="relative">
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="Enter your email"
                className="w-full border rounded-lg px-4 py-3 pl-10 text-sm"
              />
              <FaEnvelope className="absolute left-3 top-4 text-gray-400" />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium mb-1">Password</label>
            <div className="relative">
              <input
                type={showPassword ? "text" : "password"}
                name="password"
                value={formData.password}
                onChange={handleChange}
                placeholder="Enter your password"
                className="w-full border rounded-lg px-4 py-3 pl-10 text-sm"
              />
              <div 
                className="absolute left-3 top-4 text-gray-400"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? <FaEyeSlash /> : <FaEye />}
              </div>
            </div>
          </div>

          <div className="text-right">
            <button type="button" className="text-sm text-blue-600">
              Forgot Password?
            </button>
          </div>

          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-3 rounded-lg text-sm font-medium"
          >
            Sign In
          </button>
        </form>

        <div className="mt-6 text-center">
          <span className="text-sm text-gray-600">
            Don't have an account?{' '}
            <button
              onClick={() => navigate('/register')}
              className="text-blue-600 font-medium"
            >
              Register
            </button>
          </span>
        </div>
      </div>
    </div>
  );

  // Desktop/Web Layout
  const WebLayout = () => (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
      <div className="bg-white p-8 rounded-xl shadow-lg w-full max-w-md">
        <div className="mb-6">
          <h2 className="text-2xl font-semibold text-center">Admin Login</h2>
          <p className="text-sm text-gray-500 text-center mt-1">Access your admin dashboard</p>
        </div>

        {error && <div className="text-red-500 text-sm mb-4 text-center">{error}</div>}

        <form className="space-y-5" onSubmit={handleSubmit}>
          {/* User Type Selection */}
          <div className="flex justify-center gap-4 mb-4">
            <button
              type="button"
              onClick={() => setFormData({...formData, userType: 'user'})}
              className={`px-4 py-2 rounded-lg ${formData.userType === 'user' 
                ? 'bg-blue-600 text-white' 
                : 'bg-gray-100 text-gray-600'}`}
            >
              User
            </button>
            <button
              type="button"
              onClick={() => setFormData({...formData, userType: 'admin'})}
              className={`px-4 py-2 rounded-lg ${formData.userType === 'admin' 
                ? 'bg-blue-600 text-white' 
                : 'bg-gray-100 text-gray-600'}`}
            >
              Admin
            </button>
          </div>

          <div>
            <label className="block text-sm font-medium mb-1">Email</label>
            <div className="relative">
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="Enter your email"
                className="w-full border rounded-md px-4 py-2 pl-10 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <FaEnvelope className="absolute left-3 top-3.5 text-gray-400" />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium mb-1">Password</label>
            <div className="relative">
              <input
                type={showPassword ? "text" : "password"}
                name="password"
                value={formData.password}
                onChange={handleChange}
                placeholder="Enter your password"
                className="w-full border rounded-md px-4 py-2 pl-10 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <div 
                className="absolute left-3 top-3.5 text-gray-400 cursor-pointer"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? <FaEyeSlash /> : <FaEye />}
              </div>
            </div>
          </div>

          <div className="text-right">
            <button type="button" className="text-sm text-blue-600">
              Forgot Password?
            </button>
          </div>

          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-3 rounded-md hover:bg-blue-700 transition duration-200"
          >
            {formData.userType === 'admin' ? 'Admin Login' : 'Sign In'}
          </button>
        </form>

        {formData.userType === 'user' && (
          <div className="mt-6 text-center text-sm text-gray-600">
            Don't have an account?{' '}
            <button
              onClick={() => navigate('/register')}
              className="text-blue-600 hover:underline"
            >
              Register
            </button>
          </div>
        )}
      </div>
    </div>
  );

  return isMobile ? <MobileLayout /> : <WebLayout />;
};

export default LoginForm;
